$('.partner').click(function(){
    if(!$(this).is(".selected")) {
        $('.partner.selected').removeClass('selected');
        $(this).addClass('selected');
        var position = $(this).position();
        $('.point').css('left',position.left + 78).hide().fadeIn('fast');
        var partner = $(this).attr('partner');
        var height = $('#main').height();
        $('#main').height(height);
        $('.details .inner').hide();
        $('.details .inner[partner='+partner+']').show();
        $('.details').hide().fadeIn('fast');
        $('#main').height('inherit');
    }
});

$('.top-btn').click(function(){
    $('html, body').animate({ scrollTop: 0 }, 0);
});

