     $(jAccountDialog.find('input').get(0)).focus();
});
$(".request-quote-btn").click(function(){
     jOverlay.show();
     jRequestDialog.show();
     $(jRequestDialog.find('input').get(0)).focus();
});
$('.unbounce').click(function(){
    bounced = true;
    jAccountDialog.removeAttr('mode');
    jAccountDialog.find(".submit-btn").click();

});

//Account Dialog Stuff ========================================================
jAccountDialog.find(".close-btn").click(function(){
    jOverlay.hide();
    jAccountDialog.hide();
});

jAccountDialog.find(".submit-btn").click(function(){
    var data = validateAccount();
    if(data) {
        //for blog?
        if(badLead(data)) {
            jAccountDialog.attr('mode','bounce');
            return;
        }

        //show wait panel
        jAccountDialog.attr('mode','waiting');
        createAccount(data, function(){
            //on created
            window.location.href = "/dashboard/?welcome"
        }, function(errorMsg) {
            //on error
            jAccountDialog.attr('mode','create');
            var msg = null;
            switch(errorMsg) {
                case 'username taken':
                    msg = 'Username already taken.';
                    jAccountDialog.find('.username').addClass('required');
                    break;
                case 'invalid username':
                    msg = 'Invalid username. Letters, numbers, and _ @ + . -';
                    jAccountDialog.find('.username').addClass('required');
                    break;
                case 'invalid email':
                    msg = 'Invalid email address.';
                    jAccountDialog.find('.email').addClass('required');
                    break;
                default:
                    msg = 'Error creating account.';
                    break;
            }
            jAccountDialog.find('.error-msg').text(msg);
        });
    }
});

var validateAccount = function() {
    $('.required').removeClass('required');
    $('.error-msg').text("");
    jAccountDialog.find("input, textarea").each(function(){
         if(!$(this).val()) {
            $(this).addClass("required");
         }
    });

    if(jAccountDialog.find('textarea.product, input.website, input.company').is('.required')) {
        $('.croc-personal-info > div').addClass('notice');
    }

    if(jAccountDialog.find(".required").length > 0) {
        return false;
    }
    else {
        var firstname = jAccountDialog.find(".firstname").val();
        var lastname = jAccountDialog.find(".lastname").val();
        var company = jAccountDialog.find(".company").val();
        var username = jAccountDialog.find(".username").val();
        var email = jAccountDialog.find(".email").val();
        var password = jAccountDialog.find(".password").val();
        var product = jAccountDialog.find(".product").val();
        var website = jAccountDialog.find(".website").val();
        /*var confirm = jAccountDialog.find(".confirm").val();
        if(password != confirm) {
            jAccountDialog.find(".password, .confirm").addClass("required");
            jAccountDialog.find(".error-msg").text("Passwords do not match.");
            return false;
        }*/
        if(email.indexOf("@") == -1) {
            jAccountDialog.find(".email").addClass("required");
            jAccountDialog.find(".error-msg").text("Invalid email address.");
            return false;
        }
    }

    return {
        firstname: firstname,
        lastname: lastname,
        company: company,
        username: username,
        email: email,
        password: password,
        product: product,
        website: website
    };
}

var badLead = function(data) {
    //already bounced?
    if(bounced) {
        //don't bounce more than once
        return false;
    }

    //used for blog?
    var message = data.product.toLowerCase();
    if(/(wordpress|blog)/.exec(message)) {
        return true;
    }

    //check top level domain
    if(/\.(info|biz)$/.exec(data.website)) {
        return true;
    }

    //check website
    if(/(wordpress|blog)/.exec(data.website)) {
        return true;
    }

    //check email
    if(/hotmail/.exec(data.email)) {
        return true;
    }

    return false;
}

var createAccount = function(account_info, oncreated, onerror) {
    $.ajax({
        url: "/webservice/acount/create",
        type: 'POST',
        data: account_info,
        error: function(jqXHR, textStatus, errorThrown) {
            var data = {};
            try {
                data = JSON.parse(jqXHR.responseText);
            } catch(ex) {}
            onerror(data.error);
        },
        success: function(data, textStatus, jqXHR) {
            if(data == "true") {
                oncreated();
            }
            else {
                onerror();
            }

        }
    });
}

//Request Quote Dialog ========================================================
jRequestDialog.find(".close-btn").click(function(){
    jOverlay.hide();
    jRequestDialog.hide();
});

jRequestDialog.find(".submit-btn").click(function(){
    var data = validateRequestInfo();
    if(data) {
        //show wait panel
        jRequestDialog.attr('mode','waiting');

        requestQuote(data, function(){
            //on created
            jRequestDialog.attr('mode','done');

        }, function() {
            //on error
            jRequestDialog.attr('mode','info');
            jRequestDialog.find('.error-msg').text('Error requesting quote.');
        });
    }
});

var validateRequestInfo = function() {
    $('.required').removeClass('required');
    $('.error-msg').text("");
    jRequestDialog.find("input, select").each(function(){
         if(!$(this).val()) {
            $(this).addClass("required");
         }
    });

    if(jRequestDialog.find(".required").length > 0) {
        return false;
    }
    else {
        var firstname = jRequestDialog.find(".firstname").val();
        var lastname = jRequestDialog.find(".lastname").val();
        var company = jRequestDialog.find(".company").val();
        var role = jRequestDialog.find(".role").val();
        var email = jRequestDialog.find(".email").val();
        var phone = jRequestDialog.find(".phone").val();
        var product = jRequestDialog.find(".product").val();
        var questions = jRequestDialog.find(".questions").val();
        var howheard = jRequestDialog.find(".howheard").val();
        if(email.indexOf("@") == -1) {
            jRequestDialog.find(".email").addClass("required");
            jRequestDialog.find(".error-msg").text("Invalid email address.");
            return false;
        }
    }

    return {
        firstname: firstname,
        lastname: lastname,
        company: company,
        role: role,
        email: email,
        phone: phone,
        product: product,
        questions: questions,
        howheard: howheard
    };
}

var requestQuote = function(data, oncreated, onerror) {
    $.post("/webservice/quote/request", data, function(response){
        oncreated();
    }).error(function(){
        onerror();
    });
}

