//Uncheck checkboxes
$('input[flag="editable"]').removeAttr('checked');
$('input[flag="downloadable"]').attr('checked', 'checked');

$(document.body).on('click', '.doc:not(.selected)', function(){
    $('.doc.selected').removeClass('selected');
    $(this).addClass('selected');
    createNewSession();

    //analytics
    if(typeof(_gaq) != "undefined") {
        _gaq.push(['_trackEvent', 'demo', 'doc-clicked', $(this).index()]);
    }
});

$(document.body).on('click', '.skin:not(.selected)',function(){
    $('.skin.selected').removeClass('selected');
    $(this).addClass('selected');
    updateViewer();

    //analytics
    if(typeof(_gaq) != "undefined") {
        _gaq.push(['_trackEvent', 'demo', 'skin-clicked', $(this).index()]);
    }
});

//Select first document
updateViewer($('.doc:first').addClass('selected').attr('readsession'));

$('input[flag]').change(function(){
    createNewSession()

    //analytics
    if(typeof(_gaq) != "undefined") {
        _gaq.push(['_trackEvent', 'demo', 'checkbox-clicked', $(this).attr('flag'), $(this).is(':checked')]);
    }
});

function createNewSession() {
    var uuid = $('.doc.selected').attr('uuid');
    var jSettings = $('#choose-options');
    var editable = jSettings.find('input[flag="editable"]').is(':checked');
    var downloadable = jSettings.find('input[flag="downloadable"]').is(':checked');

    $.ajax({
        type: 'POST',
        url: '/webservice/session/create',
        data: { 'demo': 'true', 'uuid': uuid, 'editable': editable, 'downloadable': downloadable, 'user': '1,Demo User' },
        dataType: 'json',
        success: function(data, textStatus, jqXHR) {
            updateViewer(data['session']);
        },
        error: function(jqXHR, textStatus, errorThrown) {
            //TODO: error handling
        }
    });
}

var currentSession = null;
function updateViewer(newSession) {
    var jViewer = $('.viewer');

    //switching from another URL?
    if(jViewer.attr('src')) {
        jViewer.css('opacity', 0.5);
    }

    var onViewerLoad = function () {
        var jViewer = $(this);

        //disable existing CSS
        jViewer.contents().find('.custom-stylesheet').each(function(i, customStyle) {
            customStyle.disabled = true;
        });

        jViewer.css('opacity', 1);

        //apply custom CSS
        var css = $.trim($('.skin.selected').text());
        var styleElement = document.createElement("style");
        styleElement.type = "text/css";
        styleElement.className = "custom-stylesheet";
        if (styleElement.styleSheet) {
            styleElement.styleSheet.cssText = css;
        } else {
            styleElement.appendChild(document.createTextNode(css));
        }
        jViewer.contents().find('head').append(styleElement);
    }

    if (newSession && newSession != currentSession) {
        jViewer.attr('src', '/view/' + newSession).load(onViewerLoad);
        currentSession = newSession;
    } else {
        onViewerLoad.call(jViewer.get(0));
    }
}

