var startDownload = function(session) {
    var url = '/webservice/document/download?session=' + session;
    $.get(url, function(data){
        data = $.parseJSON(data);
        if(data.taskuuid) {
            //start download
            var taskuuid = data.taskuuid;
            var serverAddress = data.serveraddress;
            document.location.href = url + '&taskuuid=' + taskuuid + '&serveraddress=' + serverAddress;
        }
        else {
            //TODO: handle download error, log?
            //error!
        }
    }).error(function() {
        //TODO: handle download error, log?
        //error!
    });
}
var displayDoc = function(session) {
    updateViewer(session);
    $('.overlay').show();
    $('.dialog').addClass('visible').show();
}
function updateViewer(newSession) {
    var jViewer = $('.viewer');

    //switching from another URL?
    if(jViewer.attr('src')) {
        jViewer.css('opacity', 0);
    }

    var onViewerLoad = function () {
        var jViewer = $(this);

        jViewer.css('opacity', 1);
    }

    jViewer.attr('src', '/view/' + newSession).load(onViewerLoad);
}

$('.more-btn').click(function(){
    $(this).hide().closest('.section').find('.details').slideDown('fast', function(){
        $(this).addClass('visible');
    });
    $(this).siblings('.hide-btn').show();

    //analytics
    if(typeof(_gaq) != "undefined") {
        var name = $(this).closest('.section').attr('name');
        _gaq.push(['_trackPageview', location.pathname + location.search + '#'+name]);
    }
});
$('.summary img').click(function(){
     $(this).closest('.summary').find('.more-btn').click();
});
$('.hide-btn').click(function(){
    $(this).closest('.section').find('.details').slideUp('fast', function(){
        $(this).closest('.section').find('.summary .hide-btn').hide().siblings('.more-btn').show();
        $(this).removeClass('visible');
    });
});
