	IS_A = false;
	DEBUG = false;

	$(function() { 
	    // setup ul.tabs to work as tabs for each div directly under div.panes
	    $("#tabs").tabs("div.panes > div", {
			onClick: function(event, tabIndex) { 
		        if(tabIndex != 0) {
					toggleUploadSWF(false);
				} else if($('#UploadPanel').is(':visible')){
					toggleUploadSWF(true);
				}
		    } 
		});
	});

	var uploaderCore = new UploaderCore(true);
	uploaderCore.addListeners({
		"onFileSelected": onFileSelected,
		"onUploadProgress": onUploadProgress,
		"onUploadSuccess": onUploadSuccess,
		"onUploadError": onUploadError,
		"onConversionQueued": onConversionQueued,
        "onConversionProgress": onConversionProgress,
		"onConversionError": onUploadError
	});

	$(document).ready(function() {
		if (swfobject.getFlashPlayerVersion().major == 0) {
			$(".upload-button").click(function(e) {
				if (confirm("Flash is required in order to upload and view documents on Crocodoc.\n\nClick Ok to download the latest version of Flash.")) {
					location.href = "http://get.adobe.com/flashplayer/";
				}
			});
		} else {
			uploaderCore.setupUploader({browse_button: 'UploadButton'});
		}
	});

	//=================================================================
	//Button Event Handlers
	//=================================================================
	$('#SuccessResetButton').click(function(){
		location.reload(true);
	});

	$('#ErrorResetButton').click(function(){
		location.reload(true);
	});

	$('#GoButton').click(function(){
		var url = $.trim($('.url-input').val());
		if(url != '') {
			//Prepending 'http://' if it's not already in the URL
			if(url.indexOf('http://') != 0 && url.indexOf('https://') != 0) {
				url = "http://"+url;
			}
			//Validate URL
			if(isValidUrl(url)) {
				onURLSubmitted(url);
			}
			else {
				$('.url-input').val(url);
				alert('This URL is invalid.');
			}
		} else {
            alert("Please enter a URL");
        }
	});

	$('.url-input').keypress(function(e){
		if(e.which == 13 && $(this).val() != '') {
			$('#GoButton').click();
		}
	});

	//=================================================================
	//Helper Methods
	//=================================================================
	if($.browser.msie && $.browser.version < 9) {
		// fade effect in IE is ugly...
		$.fx.off = true;
	}

	var jShowing = $('#UploadPanel');
	var fadeQueue = [];
	var fadeCallbackQueue = [];
	var fading = false;

	var fade = function(jTo, callback) {
        if (jTo) {
            fadeQueue.push(jTo); 
		    fadeCallbackQueue.push(callback);
		}

		if (!fading) {
			fading = true;
			jShowing.fadeOut('fast', onFadeOutDone);
		}
	}

	var onFadeOutDone = function() {
		var jNext = fadeQueue.shift();
		if (jNext) {
			jNext.fadeIn('fast', onFadeInDone);
		}
	}

	var onFadeInDone = function() {
		jShowing = $(this);
		var callback = fadeCallbackQueue.shift();
		if (callback) {
			callback();
		}

		fading = false;
		if (fadeQueue.length > 0) {
			//var jNext = fadeQueue.shift();
			//var nextCallback = fadeCallbackQueue.shift();
			fade();
		}
	}

	var progressBar = $("#ProgressBar").progressbar();
	$('.url-input').watermark('Paste web page URL here');
    var convProgBar = $("#ConvertProgressBar").progressbar();

	function isValidUrl(str) {
		var pattern = /^(http|https):\/\/[a-z0-9]+([\-\.]{1}[a-z0-9]+)*\.[a-z]{2,5}(([0-9]{1,5})?\/.*)?$/;
		var pattern2 = /\d+\.\d+\.\d+\.\d+/;
		if(pattern.test(str) || pattern2.test(str) || str.indexOf('localhost:') != -1) {
			return true;
		} else {
			return false;
		}
	}

	var logClientError = function(errorMessage) {
		var errorData = 'errormessage='+errorMessage;
		$.post('/webservice/document/logclienterror?'+errorData,function(sResponse){
			var response = JSON.parse(sResponse);
			if(response == true) {
				//do nothing, error logged successfully
			}
			else {
				//TODO: error logging error.  Oops.
			}
		});
	}

	//=================================================================
	//Upload and Conversion Handlers
	//=================================================================
	var trackFile = {
		name: '',
		type: '',
		size: null,
		from: ''
	}
	var tUploadStart = null;
	var tConversionStart = null;
	var bUploadSuccess = undefined;
	var bConversionSuccess = undefined;


	function onFileSelected(file) {
		//toggleUploadSWF(false);

		$('#ProgressBar').show();
		fade($('#WaitingPanel'));
	}

	function onUploadProgress(file) {
		$("#ProgressBar").progressbar("option", "value", file.percent);
	}

	function onURLSubmitted(url) {
		$('#WaitMessage').show();
		//toggleUploadSWF(false);
		$('#UploadingText').text('Creating snapshot...');

		uploaderCore.submitURL(url);

		fade($('#WaitingPanel'));
	}

    var last_filename = null;
    var last_pagecount = -1;
	function onUploadSuccess(data) {
		var uuid = data.uuid;
		last_filename = data.filename;
		last_pagecount = data.page_count;
		fade($('#ConvertingPanel'));

	    convProgBar.progressbar('option', 'value', 0);	
	}

	function onUploadError(errorCode, errorMsg) {
		$('#ErrorMessage').text(errorMsg);
        fade($('#ErrorPanel'));
	}

	function onConversionQueued(data) {

        // estimate conversion time
		extension = last_filename.split('.').pop();
		var expectedTime = 20000;
		if(extension == 'pdf') {
            expectedTime = 8000;
			//expectedTime = pageCount == -1 ? 30000 : Math.max(3000, pageCount*1200);
		} else if (extension == 'png' || extension == 'jpg') {
            expectedTime = 5000;
        }

		startAnimateProgBar(convProgBar, 0, 80, expectedTime, function(){
			// fallback 1
			startAnimateProgBar(convProgBar, 80, 90, 25000, function(){
				// fallback 2
				startAnimateProgBar(convProgBar, 90, 95, 40000, null);			
			});
		});


	}

    function onConversionProgress(info) {
        var curProgress = convProgBar.progressbar('option', 'value');

		startAnimateProgBar(convProgBar, curProgress, 100, 500, function() {
			var docLocation = 'http://' + location.host + '/' + info.document.shortId;
			location = docLocation;
		});
    }

	function onConversionError(errorCode, errorMsg) {
		stopAnimateProgBar(convProgBar);

		$('#ErrorMessage').html(errorMsg);
		fade($('#ErrorPanel'));
	}

	function toggleUploadSWF(show) {
		if(show) {
			//$('#SWFButtonPlaceholderUploader').width(278);
			//$('#SWFButtonPlaceholderUploader').height(48);
			$('div.plupload.flash').width(278);
			$('div.plupload.flash').height(48);
			uploaderCore.refresh();
		}
		else {
			//$('#SWFButtonPlaceholderUploader').width(1);
			//$('#SWFButtonPlaceholderUploader').height(1);
			$('div.plupload.flash').width(1);
			$('div.plupload.flash').height(1);
			$('div.plupload.flash').offset({ top: 1, left: 1 });
		}
	}

    var animateIntervalID = -1;
	function startAnimateProgBar(progBar, startPercentInt, finishPercentInt, durationMs, onComplete) {
		var UPDATE_INTERVAL = 50;
		var startTime = (new Date()).getTime();

		clearInterval(animateIntervalID);
        var progress = startPercentInt;
		progBar.progressbar('option','value',progress);
        progBar.show();

        animateIntervalID = setInterval(function() {
			var elapsedTimeMs = (new Date()).getTime() - startTime;
			progress = Math.min(100, startPercentInt + Math.ceil(((elapsedTimeMs / durationMs) * (finishPercentInt - startPercentInt))));

            progBar.progressbar('option','value',progress);
			if (progress >= finishPercentInt) {
                clearInterval(animateIntervalID);
				if (typeof(onComplete) == "function") {
					onComplete();
				}
            }
        },UPDATE_INTERVAL);
	}	

	function stopAnimateProgBar(progBar) {
		clearInterval(animateIntervalID);
	}

	window.onbeforeunload = function() { 
		if(tUploadStart && bUploadSuccess === undefined) {
			//still uploading
			trackFile.u_duration = Math.floor((new Date() - tUploadStart)/1000);
			track('upload-abandoned',trackFile);
		} else if(tConversionStart && bConversionSuccess === undefined) {
			//still converting
			trackFile.c_duration = Math.floor((new Date() - tConversionStart)/1000);
			track('conversion-abandoned',trackFile);
		}
	}

