var track = function(event,data) {
	//log
	if(event == 'upload-error' || event == 'upload-abandoned' || event == 'conversion-error' || event == 'conversion-abandoned' || event == 'conversion-success') {
		var msg = {		
			messageType:1,
			message: 'client-'+event + ' ' + JSON.stringify(data).replace('&','%26')
		};
		$.ajax({
			url: '/webservice/log/log',
			type: 'POST',
			dataType: 'json',
			contentType: "application/json; charset=utf-8",
			data: "msgObj="+JSON.stringify(msg)
		});	
	}


	//google
	var value = (data.c_duration != null) ? data.c_duration : data.u_duration;
	var action = (value == null) ? ['_trackEvent', event, data.from, data.type] : ['_trackEvent', event, data.from, data.type, value];
	_gaq.push(action);

}

