    setTimeout(function(){
        if(location.hash == "#redirect") {
            if(typeof(doc) != "undefined" && doc.readonly == true) {
                //don't show notification on read-only document
                return;
            }

            if(location.search.indexOf("embedded") != -1) {
                //don't show notification on embedded doc
                return;
            }

            if(location.href.indexOf("/accounts/login") == -1) {
                var url = location.href.replace("#redirect","");            
            }
            else {
                var url = "http://personal.crocodoc.com/";
            }
            $('.redirect-notification .link-box a').attr("href", url).text(url);
            $('.redirect-notification').show().animate({ top : '+=300' }, 300, "swing");
            $('.redirect-notification .hide-btn').click(function(){
                $('.redirect-notification').animate({ top : '-=300' }, 300, "swing", function(){
                    $(this).hide();
                });
            });
        }
    },300);
});

