/inline_0b6bde7d9d6ad096488b9035f0885d43.body:
$(document).ready(function(){

	// hide #back-top first
	$("#back-top").hide();

	// fade in #back-top
	$(function () {
		$(window).scroll(function () {
			if ($(this).scrollTop() > 150) {
				$('#back-top').fadeIn();
			} else {
				$('#back-top').fadeOut();
			}
		});

		// scroll body to 0px on click
		$('#back-top a').click(function () {
			$('body,html').animate({
				scrollTop: 0
			}, 800);
			return false;
		});
	});

});

/inline_5c84df5b074798e22f5b7f2efa951197.body:
$(document).ready(function() { 

	$('.subscriptions-signup').click(function(e) {
		$('#subscribe-modal').modal('show');
		e.preventDefault();
	});

	$('#subscriptions-email-form').submit(function(e) {
		var form = $(this);
		e.preventDefault();
		if ($('#subscriptions-email').val().trim() == '') {
			$('#subscriptions-error').text('Please enter your email address').fadeIn();
		}
		else if ($('#subscriptions-email-form input[name=subscriptions]:checked').length < 1) {
			$('#subscriptions-error').text('Please select at least one category to subscribe to').fadeIn();
		}
		else {
			var subscriptions = new Array();
			$('#subscriptions-email-form input[name=subscriptions]:checked').each(function() { subscriptions.push($(this).val()) });

			$.ajax({
				type: 'POST',
				async: false,
				dataType: 'json',
				url: form.attr('action'),
				data: { 
					email: $('input[name=email]', form).val(),
					subscriptions: subscriptions,
					csrf: $('input[name=csrf]').val()
				},
				success: function(data) {
					if (data.new_user) {
						window.location = '/invite'
					}
					else {
						login_action = $('input:submit', form);
						$('#subscribe-modal').modal('hide');
						$('#loginModal .modal-header h1').text('Sign into your account first');
						$('#loginModal').modal('show');
					}
				}
			});
		}
	});
});

