$(document).ready(function() {

	$('#save-email a').click(function() {
		$('#save-email').fadeOut(function() {
			$('#save-email-actions').slideDown();
		});
	});

	$('.btn-refer').live('click', function(e) {
		e.preventDefault();
		$.post($(this).attr('action'), {
			url: $(this).attr('rel')
		}, function(data) {
			shareModal.init(data);
			$('#hash-field').val(data.hash);
		}, 'json');
	});

	$('#share-twitter').click(function(e) {
		e.preventDefault();
		window.open($(this).prop('href'), '_blank', 'width=425,height=260,titlebar=yes,status=no,location=no,scrollbars=no');
	});

	$('#share-facebook').click(function(e) {
		e.preventDefault();
		window.open($(this).prop('href'), '_blank', 'width=626,height=436,toolbar=0,status=no,location=no,scrollbars=no');
	})
});

var shareModal = {
	intervalId: 0, // id from setInterval, used to cancel the interval later.
	intervalCount: 0, // running counter.
	intervalTime: 250, // time between polling intervals.
	intervalMax: 10, // maximum number of requests.
	init: function(link) {
		if (! (/ Android|webOS|iPhone|iPad|iPod|BlackBerry/i.test(navigator.userAgent))) {
			$('#ghostLinkContainer').css('position', 'absolute');
			$(window).bind('resize', shareModal.reposition);
		}
		$('#ghostLinkCloseButton').bind('click', shareModal.cleanup);
		$('#ghostLinkOverlay').css('height', $(document).outerHeight());
		$(document).bind('keyup', shareModal.handleKeyup);

		// Start loading link details.
		this.intervalId = setInterval(function() {
			shareModal.pollForValues(link.hash);
		}, this.intervalTime);
		shareModal.showModal();
		$('html, body').animate({scrollTop:0}, 'slow');
	},
	showModal: function() {
		$('#loaded', '#ghostLinkContent').slideDown();
		$('#ghostLinkContainer').show();
		shareModal.reposition();
	},
	pollForValues: function(hash) {
		var that = this;
		this.intervalCount = this.intervalCount + 1;
		$.get('/links/'+ hash +'/show', function(data) {
			if (data.link['last_gathered_at'] != null || that.intervalCount > 10) {
				that.updateValues(data.link);
				clearInterval(that.intervalId);
			}
		}, 'json');
	},
	updateValues: function(link) {
		var twitter_url = "https://twitter.com/share?url="+ link.url;
		var facebook_url = "http://www.facebook.com/sharer.php?u="+ encodeURIComponent(link.url) +"&t="+ encodeURIComponent(link.title);
		var $success = $('#ghostLinkContent');
		$success.find('.title').text(link.title);
		$success.find('.original-url').text(link.original_url);
		$('#copy-url').val(link.url);
		$('#share-twitter').attr('href', twitter_url);
		$('#share-facebook').attr('href', facebook_url);
		this.bindCopy();
	},
	reposition: function() {
		var winHeight = $(window).height();
		var winWidth = $(window).width();
		$('#ghostLinkContainer').css('height', winHeight).css('width', winWidth);
		var top = (winHeight - $('#ghostLinkContent').height()) / 2;
		var left = (winWidth - $('#ghostLinkContent').width()) / 2;
		$('#ghostLinkContent').css('top', top).css('left', left);
		$('#ghostLinkCloseButton').css('top', top - 15).css('left', left + $('#ghostLinkContent').width() - 15);
		this.bindCopy();
	},
	bindCopy: function() {
		$('#copy-url-button').zclip({
			path: "//s3.amazonaws.com/referly_assets/assets/js/ZeroClipboard.swf",
			copy: function(){
				return $('#copy-url').val();
			},
			afterCopy: function() {}
		});
	},
	handleKeyup: function(e) {
		if (e.keyCode === 27) {
			shareModal.cleanup();
		}
	},
	cleanup: function() {
		$('#ghostLinkContainer').hide();
		$('#loaded', '#ghostLinkContent').hide();
		$(window).unbind('resize', shareModal.reposition);
		$(document).unbind('keyup', shareModal.handleKeyup);
	}
}

