$('#signupModal').on('shown', function () {
	setCookie('signup_source', 'signup_modal', 1);
});

$('#save-email a').click(function() {
	$('#save-email').fadeOut(function() {
		$('#save-email-actions').slideDown();
	});
});

$('#save-email-form').submit(function(e) {
	if (window.location.protocol != 'https:') {
		return; // AJAX not supported across protocols, so let browser handle this
	}

	e.preventDefault();
	$.post($(this).attr('action'), {
		email: $('input[name=email]', this).val(),
		password: $('input[name=password]', this).val(),
		subscribe: $('input[name=subscribe]', this).is(':checked') ? 1 : 0,
		csrf: $('input[name=csrf]').val()
	}, function(data) {
		if (data['status'] === 'success' || data['status'] === 'authenticated') {
			if (login_action != '') {
				$(login_action).click();
			}
			if (data['redirect']) {
				window.location = '/' + data['redirect'];
			}
			else {
				window.location = '/links/tour';
			}
		}
		else {
			$('#save-email-error').fadeIn();
		}
	}, 'json');
});

$(document).on('click', "#modal_referly_facebook_login, #referly_facebook_login, #facebook-button, #logged_out_facebook_hammer", function() {
	FB.Event.subscribe('auth.statusChange', function(response) {
		if (response.authResponse) {
			window.location = "https://refer.ly/fb_login";
		}
	});

	FB.getLoginStatus(function(response) {
		if (response.status === 'connected') {
			window.location = "https://refer.ly/fb_login";
		}
		else {
			FB.login(function(response){},{scope:'email'});
		}
	});
});

