$('#login_submit').click(function(event) {
	if (window.location.protocol == 'https:') {
		event.preventDefault();
		$.ajax({
			url: 'https://refer.ly/login',
			type: 'POST',
			data: {
				email: $('#login_email').val(),
				password: $('#login_password').val(),
				redirect: ""							},
			dataType: 'json',
			success: function(data) {
				if (data.status == 'authenticated') {
					if (login_action.length) {
						login_action.click();
					}

					if ($("input[name='redirect-location']").length && $("input[name='redirect-location']").val().length) {
						window.location = $("input[name='redirect-location']").val();
					}
					else if (data.redirect_uri) {
						window.location = data.redirect_uri;
					}
					else {
						window.location = '/friend_feed';
					}
				}
				else if (data.errors) {
					$('#login_warning_area').html('<div class="alert"><button class="close" data-dismiss="alert" style="font-size:16px; color:white;">x</button><strong>Error: </strong> ' + data.errors + '</div>');
				}
			},
			error: function(jqXHR, textStatus, errorThrown) {
			}
		});
	}
});

