$(function () {
	$(".thumbnail").popover({
		trigger: "click",
		container: "body",	
		placement: function (context, content) {
			// random bootstrap bug is random
			_.defer(function () { $(context).css("top", $(content).position().top - 150); });
			return "right";
		},
		content: function () {
			// relies on there being a /path/to/filename@2x.png, if
			// data-content is /path/to/filename.png
			var path = $(this).attr('data-content');
			if (window.devicePixelRatio >= 1.5) {
				var chunks = path.split('.');
				var preExtension = chunks[chunks.length-2];
				chunks.splice(chunks.length - 2, 1, preExtension + '@2x');
				path = chunks.join(".");
			}

			if (path) {
				return "<img src='"+path+"' />";
			} else {
				return false;
			}
		},
		html: true
	});


});

