$(document).ready(function(){
	SmallTab = $(".slide-out");
	$(SmallTab).mouseenter(function() {
		SmallTab = this;
		$(SmallTab).animate({width: "150px"}, { queue:false, duration:400 });
		}).mouseleave(function() {
		$(SmallTab).animate({width: "40px"}, { queue:false, duration:400 });
	});
});

$(document).ready(function(){
	LargeTab = $(".slide-out-large");
	$(LargeTab).mouseenter(function() {
		LargeTab = this;
		$(LargeTab).animate({width: "350px"}, { queue:false, duration:400 });
		}).mouseleave(function() {
		$(LargeTab).animate({width: "40px"}, { queue:false, duration:400 });
	});
});

$(function() {$(".NewsletterSubscribe").click(function() {
		$(".slide-out-large").animate({width: "350px"}, { queue:false, duration:400 });
		$("#mc_mv_EMAIL").focus();
		return false;
	});
});

