// homepage.js

(function() {

  function statSlider () {

    var advanceTime       = 5000
    var autoAdvance       = true

    setInterval(function (){

      if ( autoAdvance ) {
        activeSlide = $('#hero-stats li.active')
        lastSlide = $('#hero-stats li.active').is(':last-child')
        activeSlide.removeClass('active')
        if ( !lastSlide ) {
          activeSlide.next().addClass('active')
        } else {
          $('#hero-stats li:first-child').addClass('active')
        }
      }
    }, advanceTime)

  }

  function videoPlayer() {

    vidAdaptableDesign = document.getElementById("video-brands")
    var neverPlayed = true
    var trigger = $('.marketing-block-brands')

    trigger.hover(function () {
      if ( neverPlayed ) {
        vidAdaptableDesign.play()
        neverPlayed = false
      }
    })      

  }


  // Doc ready

  $(document).ready(function() {
    statSlider();
    videoPlayer();
  })

})();

// gravity.js

(function (){
var dataset;
var datasetAside;

var baseRadius         =  230;
var setScale = function (d, scale) {
  d.scale = scale;
  d.radius = baseRadius * scale;
};

var setPosition = function (d) {
  return this.style("left", function (d) {
    return parseInt(d.x - baseRadius) + "px";
  })
  .style("top", function (d) {
    return parseInt(d.y - baseRadius) + "px";
  });
};

function gravity(json) {
  if (!$('body').hasClass('loaded')) {
    // don't start we set the heights and stuff
    $(document).ready(function () { gravity(json); });
    return
  }
  // Referencing data and calling generateGravity inside the
  // callback ensures data has loaded first
  if (json)
    dataset = json;

  generateGravity();

  function generateGravity() {

    // Setup
    // -----------------------------------

    var
      // Visualization physics
      friction           =  .09,     // Range 0-1, affects node velocity decay, 1 corresponds to a frictionless environment
      focusAttraction    =  3,     // Gravitational pull of category-specific gravitational fields
      gravity            =  1,     // Canvas-wide gravitational pull toward center
      linkStrength       =  .5,       // Range: 0-1
      linkDistance       =  25,       // Length of links in pixels

      // Node scale
      // Values are used to resize nodes using CSS3 "scale"
      minSize            =  .07,
      maxSize            =  .25,
      minSizeHover       =  maxSize + .10,
      maxSizeHover       =  maxSize + .20,
      clickedSize        =  1,

      // Canvas variables
      canvas             =  d3.select("#gravityCanvas")
      viewportWidth      =  $(window).width(),
      canvasHeight       =  $('#gravityCanvas').height(),
      horizontalCut      = .15,
      horizontalDeviation= .05,
      verticalCut        = .35,
      node               =  canvas.selectAll("div")

      // Splicing variables
      nodeQtyPhone       =  10,
      nodeQtyTablet      =  20,
      nodeQtyDesktop     =  30,
      nodeQtyLarge       =  40,
      nodeQtyJumbo       =  50,

      sizePhone          =  568,
      sizeTablet         =  950,
      sizeDesktop        =  1100,
      sizeJumbo          =  1400,

      // Sets up some global variables
      activeNode         =  false,
      activeEasterEgg    =  false,
      vis                =  null,
      force              =  null,
      nodes              =  [],
      links              =  [],
      runUpdate          =  true,
      nodeAddTimeout     =  15000,
      nodeAdderDelay     =  5000,
      extraDataLimit     =  10,

      //Mobile variables
      isMobile           =  false;

    if (viewportWidth <= sizePhone) {
      $('body').addClass("mobile-device");

      isMobile = true;

      var
        minSize            =  .03,
        maxSize            =  .15,
        minSizeHover       =  maxSize,
        maxSizeHover       =  maxSize + .10,
        clickedSize        =  .5,

        nodeAddTimeout     =  10000,
        nodeAdderDelay     =  10000,
        extraDataLimit     =  5;
    }

    // Find min and max number of comments in the dataset
    var minActivity = d3.min(dataset, function(d) { return d.activity_score; });
    var maxActivity = d3.max(dataset, function(d) { return d.activity_score; });

    // Set scale to be used for radius, based on comment counts
    //var radiusScale = d3.scale.log()
    var radiusScale = d3.scale.pow().exponent(.5)
      .domain([ minActivity, maxActivity ])
      .range([minSize, maxSize]);

    var radiusScaleHover = d3.scale.pow().exponent(.5)
      .domain([ minActivity, maxActivity ])
      .range([minSizeHover, maxSizeHover]);

    // Create an ordinal scale which will later be applied to nodes as a CSS class, based on category name
    var colorScale = d3.scale.ordinal()
      .range([1,2,3,4,5]);

    // Create a scale to chunk data into 4 categories based on comment count
    var popScore = d3.scale.log()
      .domain([ minActivity, maxActivity ])
      .rangeRound([1,4]);

    // Identify unique category names in the data
    var categories = _.chain(dataset).pluck('category').uniq().value();

    // Create gravitational points in the viewport for number of categories

    var usableWidth = Math.round(viewportWidth * (1 - horizontalCut)),
      sections = categories.length - 1,
      step = Math.floor( usableWidth / sections ),
      yCoords = [],
      xCoords = _.map(_.range(Math.floor(viewportWidth * horizontalCut / 2), viewportWidth, step), function (c) {
        return _.random(c * (1 - horizontalDeviation), c * (1 + horizontalDeviation));
      });
      xCoords.splice(categories.length);


    _.times(xCoords.length, function () {
      yCoords.push(_.random(canvasHeight * verticalCut, canvasHeight * (1 - verticalCut)))
    });

    // Create category label datapoints
    var catDict = {};

    _.each(categories, function (c, ind) {
      catDict[c] = {
        node: {
          id: 'cat-' + c,
          category: c,
          radius: 1,
          title: c,
          title_truncated: c,
          publisher: c,
          className: 'category-label',
          star: false,
          activity_score: 50
        },
        x: xCoords[ind],
        y: yCoords[ind]
      };
    });

    // Ste a starting position for nodes
    var n = dataset.length;
    dataset.forEach(function(d, i) {
      d.x = d.y = canvasHeight / n * i;
    });

    if (viewportWidth < sizePhone) {datasetAside = dataset.splice(nodeQtyPhone)}
    else if (viewportWidth < sizeTablet) {datasetAside = dataset.splice(nodeQtyTablet)}
    else if (viewportWidth < sizeDesktop) {datasetAside = dataset.splice(nodeQtyDesktop)}
    else if (viewportWidth < sizeJumbo) {datasetAside = dataset.splice(nodeQtyLarge)}
    else {datasetAside = dataset.splice(nodeQtyJumbo)}
    datasetAside.splice(extraDataLimit);

    /*
    console.log(dataset.length);
    console.log(viewportWidth);
    */

    // Add new category datapoints to the dataset
    _.each(catDict, function (c) {
      dataset.push(c.node);
    });

    function processNode(d) {
      if (!d.star) {
        setScale(d, Math.round(radiusScale(d.activity_score)*1000)/1000);
        d.hoverRadius    =  Math.round(radiusScaleHover(d.activity_score)*1000)/1000;
        d.originalRadius =  d.scale; // Duplicate radius data, so that it can be referenced later on when we start messing with the radius
        d.thisClicked    =  false;
        d.thisPopulated  =  false;
        d.thisCommentAdd =  false;
        d.image_url      =  d.image_url + "&w=250";
      } else if (d.star) {
        setScale(d, _.random(1, 10) / 500);

      };

      links.push({ source: d, target: catDict[d.category].node });

      return d;
    }

    $.each(dataset, function (i, d) {
       processNode(d);
    });

    // Force layout
    // -----------------------------------

    // Charge
    charge = function(d) {
      var dA = Math.round(d.radius * 6);
      return -(dA * dA) * 1.5;
    }

    // Charge Alt
    chargeAlt = function(d) {
      var dA = Math.round(d.radius * 5);
      return -(dA * dA) * 1.5;
    }

    // Node dragging behavior
    var nodeDrag = d3.behavior.drag()
      .on("dragstart", dragstart)
      .on("drag", dragmove)
      .on("dragend", dragend);

    function dragstart(d, i) {
      d.fixed = true;
      d.dragging = true;
    }

    function dragmove(d, i) {
      var dx = d3.event.dx;
      var dy = d3.event.dy;
      d.px += dx;
      d.py += dy;
      d.x += dx;
      d.y += dy;
      force.on("tick", tick)
      force.start();
    }

    function dragend(d, i) {
      if (d.thisClicked) {
        d.fixed = true;
      } else {
        d.fixed = false;
      }
      d.dragging = false;
      force.start();
    }

    // Set up canvas
    var canvas = canvas
      .style("height", canvasHeight + "px")
      .style("width", viewportWidth + "px");

    var // Initialize the force layout
      force = d3.layout.force()
        .size([viewportWidth, canvasHeight])
        .links(links)
        .linkStrength(linkStrength)
        .linkDistance(linkDistance)
        .gravity(gravity)
        .friction(friction)
        .charge(this.charge)
        .on("tick", tick)
        .nodes(dataset)
        .alpha(.8);

    start(true);

    // Collapse expanded nodes on canvas click
    $("#gravityCanvas").on("click", ".node", function(evt) {
      evt.stopPropagation();
      if (evt.target.nodeName === "A") {
        return;
      }
      clickNode.call(this, d3.select(this).data()[0]);
    });

    $("#gravityCanvas").on("click", function(evt) {
      if (activeNode) {
        collapseNode(activeNode.html, activeNode.data);
        activeNode = false;
      }
    });

    // Set up nodes, then call force.start
    function start(initial) {
      node = node.data(force.nodes());

      nodes = node
        .enter()
        .append("div")
          .attr("class", function(d) {
            var className = "";
            className += "node color-class-" + colorScale(d.category);
            if (d.className) { className += " " + d.className; }
            else { className += " pop-" + popScore(d.activity_score); }
            if (!initial) { className += " new-node new-indicator"; }
            return className;
          })
          .attr("id", function(d) { return d.id; });

      if (initial) {
        nodes = nodes
          .style("transform", "scale(0)")
          .style("-webkit-transform", "scale(0)")
      } else {
        nodes = nodes
          .style("transform", "scale(.01)")
          .style("-webkit-transform", "scale(.01)")
      }

      var events = {};
      var cancelEvent = function (d) {
        if (!events[d.id])
          return;

        clearTimeout(events[d.id]);
        return true;
      };
      nodes = nodes
        .on("mouseover", function (d) {
          var self = this;
          cancelEvent(d);
          events[d.id] = setTimeout(function () { events[d.id] = null; mouseoverNode.call(self, d); }, 100);
        })
        .on("mouseout", function (d) {
          if (cancelEvent(d) || d.dragging)
            return;

          mouseoutNode.call(this, d);
        })
        .call(nodeDrag);

      nodes.filter(function(d) { return d.image_url; })
        .append("div")
        .attr("class", "article-image")
        .attr("style", function (d) {
          return "background-image: url(" + d.image_url + ")";
        })

      var ellipses = nodes.append("div").attr("class", "ellipse");

      ellipses.append("h2")
        //.html(function(d) { return d.title_truncated; })
