  function customSlider() {

    var 
      sliderTrigger      = $('#slider-controls li'),
      slides             = $('#slider > div.slide'),

      slide1             = $('.slide-1'),
      slide2             = $('.slide-2')
      slide3             = $('.slide-3')
      slide4             = $('.slide-4'),

      // Videos
      vidAdaptableDesign = document.getElementById("video-adaptable-design"),
      vidModeration      = document.getElementById("video-moderation");


    var items = sliderTrigger.on('click', function() {
      var index = items.index(this);
      if      ( index == 0 ) { showSlide1(this) } 
      else if ( index == 1 ) { showSlide2(this) } 
      else if ( index == 2 ) { showSlide3(this) } 
      else if ( index == 3 ) { showSlide4(this) }
    })

    // Adaptable Design
    function showSlide1(navItem) {
      slides.addClass('hidden-slide');
      slide1.removeClass('hidden-slide');

      sliderTrigger.removeClass('active');
      $(navItem).addClass('active');
    }

    var neverPlayed = true;

    $(window).scroll(_.debounce(function() {
      var 
        featuresOffset    = $('#features-slider-wrapper').offset().top,
        travelDistance    = featuresOffset-scrollPosition;

      if ( (travelDistance < 350) && (travelDistance > 0) && (neverPlayed) ) {
        setTimeout(function() {
          vidAdaptableDesign.play();
          neverPlayed = false;
        }, 500)
      }

    }, 1000));

    // Real Time
    function showSlide2(navItem) {
      slides.addClass('hidden-slide');
      slide2.removeClass('hidden-slide');

      sliderTrigger.removeClass('active');
      $(navItem).addClass('active');
    }

    // Media Embedding
    function showSlide3(navItem) {
      slides.addClass('hidden-slide');
      slide3.removeClass('hidden-slide');

      sliderTrigger.removeClass('active');
      $(navItem).addClass('active');

      setTimeout(function() {
        vidAdaptableDesign.play();
      }, 1000)
    }

    // Moderation Tools
    function showSlide4(navItem) {
      slides.addClass('hidden-slide');
      slide4.removeClass('hidden-slide');

      sliderTrigger.removeClass('active');
      $(navItem).addClass('active');

      setTimeout(function() {
        vidModeration.play();
        setInterval(function() {
          vidModeration.play();
        }, 8000)
      }, 500)
    }

  }



