(function() {

  function positionIndicator() {
    var active = $('#promoted-discovery .carousel-indicators .active');
    var activePosition = active.position();
    var activeWidth    = active.width();
    var indicator = $('#promoted-discovery .carousel-indicators #indicator');
    indicator.css({
      'left'  : activePosition.left + 'px',
      'width' : activeWidth + 61
    });
  }
  jQuery( document ).ready(function($) {
    setTimeout(function(){positionIndicator()},100);
    $( window ).resize(function() {
      positionIndicator();
    });
    $('.carousel-indicators').on('click','.link',function(){
      $('.carousel-indicators .link').css('color','#fff');
      var el = $(this).data('name');
      switch(el) {
        case 'read':
          color = '#a177f5';
          break;
        case 'comment':
          color = '#ff7f48';
          break;
        case 'consume':
          color = '#8ecf6c';
          break;
      }
      $(this).css('color',color);
      var indicator = $('#promoted-discovery .carousel-indicators #indicator');
      activePosition  = $(this).position();
      activeWidth   = $(this).width();
      indicator.removeClass();
      indicator.addClass('behind-'+$(this).data('name'));
      indicator.css({
        'left'  : activePosition.left + 'px',
        'width' : activeWidth + 61
      });
    });
  });

  // Doc ready
  $(document).ready(function() {
    positionIndicator();
  })


})();

