(function() {

  function statSlider () {

    // identify slider elements on the page with variiabls
    var buttonNext        = $('#slide-button-next')
    var buttonPrev        = $('#slide-button-prev')
    var advanceTime       = 2000
    var autoAdvance       = true

    // after a certain duration
    setInterval(function (){

      // if left or right buttons have not been used
      if ( autoAdvance ) {
        // advance to next slide

        // detrmine active slide
        activeSlide = $('#slider li.active')

        // determine if active slide is last slide
        lastSlide = $('#slider li.active').is(':last-child')

        // remove active class from it
        activeSlide.removeClass('active')

        // if it's not the last slide
        if ( !lastSlide ) {

          // figure out next slide and add class active
          activeSlide.next().addClass('active')
        } else {
          // go to the beginning and add class active
          $('#slider li:first-child').addClass('active')
        }
      }
    }, advanceTime)


    // on left button click
    buttonPrev.click( function () {
      // set autoAdvance to false
      autoAdvance = false

        // detrmine active slide
        activeSlide = $('#slider li.active')

        // determine if active slide is first slide
        firstSlide = $('#slider li.active').is(':first-child')

        // remove active class from it
        activeSlide.removeClass('active')

        // if it's not the last slide
        if ( !firstSlide ) {

          // figure out next slide and add class active
          activeSlide.prev().addClass('active')
        } else {
          // go to the beginning and add class active
          $('#slider li:last-child').addClass('active')
        }
    })

    // on right button click
    buttonNext.click( function () {
      // set autoAdvance to false
      autoAdvance = false

        // detrmine active slide
        activeSlide = $('#slider li.active')

        // determine if active slide is last slide
        lastSlide = $('#slider li.active').is(':last-child')

        // remove active class from it
        activeSlide.removeClass('active')

        // if it's not the last slide
        if ( !lastSlide ) {

          // figure out next slide and add class active
          activeSlide.next().addClass('active')
        } else {
          // go to the beginning and add class active
          $('#slider li:first-child').addClass('active')
        }
    })




  }


  // Doc ready

  $(document).ready(function() {
    statSlider ()

    $('.lazy').lazyload({
      effect: "fadeIn"
    })

  })


})();

