    'use strict';

    var attributeNames = [
        'cookieVersion',
        'userName',
        'isStaff',
        'hasForums',
        'hasDatetimeFormatting',
        'tzOffset',
        'userId',
        'avatarUrl',
        'moderatedForumCount'
    ];

    function getAttributesFromString (string) {

        // Attributes are separated on `|`
        var attributes = {};

        if (string) {
            var values = string.replace(/^"|"$/g, '' ).split('|');

