            values.push(attrs[attributeNames[i]]);
        }

        return values.join('|');
    }

    var Session = Backbone.Model.extend({

        user: new Backbone.Model(),

        initialize: function () {
            var cookieString = cookies.read('disqusauth');
            this.user.set(getAttributesFromString(cookieString));
        },

        clear: function () {
            cookies.erase('disqusauth', {});
            this.user.clear();
            Backbone.Model.prototype.clear.apply(this, arguments);
        },

        persist: function (attrs) {
            this.user.set(attrs);
