(function (global, $) {

  function _request(method, path, data, callback) {

    // Process scheme programmatically as this value may be replaced during build.
    var adminEndpoint = "https://admin.firebase.com";
    if (adminEndpoint.indexOf("//") == 0) {
      adminEndpoint = "https:" + adminEndpoint;
    }

    // Convert first level of deep data structures to foo[bar]=baz syntax.
    for (var key in data) {
      if (typeof data[key] === 'object' && data[key] !== null) {
        var o = data[key];
        delete data[key];
        for (var k in o) {
          var new_key = key + "[" + k + "]";
          data[new_key] = o[k];
        }
      }
    }

    var xhr = $.ajax({
      async       : true,
      dataType    : 'json',
      type        : method,
      url         : adminEndpoint + path,
      data        : data
    }).done(function(data, textStatus, jqXHR) {
      callback(data);
    });

    return xhr;
  }

  function get(path, data, callback) {
    return _request('GET', path, data, callback);
  }

  function post(path, data, callback) {
    return _request('POST', path, data, callback);
  }

  function put(path, data, callback) {
    data._method = 'PUT';
    return post(path, data, callback);
  }

  function del(path, data, callback) {
    data._method = 'DELETE';
    return post(path, data, callback);
  }

  function getAdminToken() {
    return $.cookie('adminToken');
  }

  var FirebaseAccount = {

    _data: null,
    _token: null,
    get: get,
    post: post,

    requestNamespace: function(firebase, callback) {
      var path = "/firebase/" + encodeURIComponent(firebase),
          params = { token: this.getAdminToken() };

      post(path, params, callback);
    },

    getAdminToken: function() {
      return $.cookie('adminToken');
    },

    setAdminToken: function(value, optDays) {
      var cookieDomain = "." + getDomain(window.location.href);
      if (window.location.hostname == 'localhost')
        cookieDomain = '';

      $.cookie('adminToken', value, {
        path: '/',
        expires: optDays,
        domain: cookieDomain,
        secure: ('https:' == document.location.protocol)
      });
    },

    ajax: function(path, data, callback) {
      var adminEndpoint = "https://admin.firebase.com";
      if (adminEndpoint.indexOf("//") == 0) {
        adminEndpoint = "https:" + adminEndpoint;
      }

      var xhr = $.ajax({
        dataType: "jsonp",
        url: adminEndpoint + path,
        data: data
      }).done(callback);

      return xhr;
    },

    login: function(email, password, rememberMe, callback) {
      var self = this;
      get("/account/login", {
        email: email,
        password: password,
        rememberMe: rememberMe
      }, function(data) {
        if (data.success && data.adminToken) {
          var sessionLengthDays = (rememberMe) ? 30 : null;
          self.setAdminToken(data.adminToken, sessionLengthDays);
          callback(true);
        } else {
          callback(false);
        }
      });
    },

    getUser: function(token, callback) {
      this.account.read(token, callback);
    },

    getNamespaceToken: function(token, firebase, callback) {
      this.firebases.token(token, firebase, callback);
    },

