        get("/account", { token: token }, callback);
      },
      update: function(data, callback) {
        data.token = getAdminToken();
        post("/account", data, callback);
      },
      token: {
        del: function(token, callback) {
          var path = "/account/token/" + encodeURIComponent(token),
              params = { token: token };

          del(path, params, callback);
        }
      },
      payments: function(callback) {
        var token = getAdminToken(),
            uid = token.split('|')[0];

        get("/account/" + encodeURIComponent(uid) + "/payments/customer", { token: token }, callback);
      },
      creditCard: function(data, callback) {
        var token = getAdminToken(),
            uid = token.split('|')[0],
            path = "/account/" + encodeURIComponent(uid) + "/payments/creditCard?token=" + encodeURIComponent(token);

        post(path, data, callback);
      }
    },
    firebases: {
      create: function(token, firebase, callback) {
        var path = "/firebase/" + encodeURIComponent(firebase),
            params = { token: token };

        post(path, params, callback);
      },
      del: function(token, firebase, callback) {
        var path = "/firebase/" + encodeURIComponent(firebase),
            params = { token: token, namespace: firebase };

        del(path, params, callback);
      },
      token: function(token, firebase, callback) {
        var path = "/firebase/" + encodeURIComponent(firebase) + "/token",
            params = { token: token, namespace: firebase };

        get(path, params, function(data) {
          var result = data.authToken || false;
          callback(result);
