      },
      stats: function(firebase, metric, callback) {
        var path = "/firebase/" + encodeURIComponent(firebase) + "/" + encodeURIComponent(metric),
            params = { token: getAdminToken() };

        get(path, params, callback);
      },
      plan: {
        update: function(firebase, planId, callback) {
          var path = "/firebase/" + encodeURIComponent(firebase) + "/plan";

          post(path, { 'plan': planId, 'token': getAdminToken() }, callback);
        }
      },
      users: {
        read: function(firebase, callback) {
          var path = "/firebase/" + encodeURIComponent(firebase) + "/users";

          get(path, { 'token': getAdminToken() }, callback);
        },
        del: function(firebase, id, callback) {
          var path = "/firebase/" + encodeURIComponent(firebase) + "/users";

