$(".tab-content").hide();

function viewportWidth(){
  var vpw;
  var webkit = (!(window.webkitConvertPointFromNodeToPage == null));
  if ( webkit ) {
    var vpwtest = document.createElement( "div" );
    vpwtest.style.cssText = "width:100% !important; margin:0 !important; padding:0 !important; border:none !important;";
    document.documentElement.insertBefore( vpwtest, document.documentElement.firstChild );
    vpw = vpwtest.offsetWidth;
    document.documentElement.removeChild( vpwtest );
  }
  else if ( window.innerWidth === undefined ) {
    vpw = document.documentElement.clientWidth;
  }
  else{
    vpw =  window.innerWidth;
  }
  return (vpw);
};

var vpw = viewportWidth();

if(vpw <= 720){
  $(".tab-container").appendTo($('.tabs li a.current').parent());
}else{
  $(".tab-container").appendTo($('tabbed-content').parent());
}

$(".tabs li:first a").addClass("current").show();
$(".tab-content:first").show();

$(".tabs li a").click(function(){
    var vpw = viewportWidth();
    if(vpw <= 720){
      $(".tab-container").appendTo($(this).parent());
    }else{
      $(".tab-container").appendTo($('tabbed-content').parent());
    }
    $(".tabs li a").removeClass("current");
    $(this).addClass("current");
    $(".tab-content").hide();

    var activeTab = $(this).attr("href");
    $(activeTab).fadeIn();
    return false;
});

$(window).resize(function() {
  var vpw = viewportWidth();

  if(vpw <= 720){
    if (!($(".tabs li .tab-container").length)) $(".tab-container").appendTo($('.tabs li a.current').parent());
  }else{
    if (!($('.tabbed-content').children('.tab-container').length)) $(".tab-container").appendTo($('.tabbed-content'));
  }
});

$("#tab1").hide();
var first_element = $("#tab" + (Math.floor(Math.random() * 5) + 1));
first_element.addClass("active").show();

$(".tabbed-content").addClass('slider').append("<div class='nav-menu'><a href='' class='prev'> </a> <a href='' class='next'> </a> </div>");

$('.slider .nav-menu a.prev').click(function(){
  var actual_element = $(".tab-container .tab-content.active");
  actual_element.removeClass('active').hide();
  if (!(actual_element.is('#tab1'))) actual_element.prev().addClass('active').fadeIn('slow');
  else $("#tab5").addClass('active').fadeIn();
  return false;
});

$('.slider .nav-menu a.next').click(function(){
  var actual_element = $(".tab-container .tab-content.active");
  actual_element.removeClass('active').hide();
  if (!(actual_element.is('#tab5'))) actual_element.next().addClass('active').fadeIn('slow');
  else $("#tab1").addClass('active').fadeIn();
  return false;
});

