$(function() {
  var __sectionImg = $(".expandable").find("img");
    $(".child").hide();
    var __sectionCollapsed = true;

  $(".expandable").click(function() {
    if (__sectionCollapsed) {
      imgPath = __sectionImg.attr("src").replace("expand", "collapse");
      $(".child").show();
    } else {
      imgPath = __sectionImg.attr("src").replace("collapse", "expand");
      $(".child").hide();
    }
    __sectionCollapsed = !__sectionCollapsed;
    __sectionImg.attr("src", imgPath);
  });
});

