/inline_966542e813f057f509c07e31c0f405b1.body:
window.onAuth = window.onAuth || [];
window.onAuth.push(function(accountData) {
  window.location = "../account/";
});

$(function() {

  // Check to see if we were referred to a Firebase landing page with a
  // Firebase invitation in the URL. If so, add a special message to the user.
  var inviteToken = getURLParameter("inviteToken");
  if (inviteToken) {
    // Be sure to escape this value from the URL before injecting it into the page.
    var firebase = $('<div/>').text(inviteToken.split('|')[0]).html();
    $("#message").html("After creating your account, the Firebase <b>" + firebase + "</b> will automatically be added to your account.").show();
  }

});

var qs = (function(a) {
  if (a == "") return {};
  var b = {};
  for (var i = 0; i < a.length; ++i)
  {
      var p=a[i].split('=');
      if (p.length != 2) continue;
      b[p[0]] = decodeURIComponent(p[1].replace(/\+/g, " "));
  }
  return b;
})(window.location.search.substr(1).split('&'));

var rootRef = new Firebase('firebase-accounts.firebaseio.com');
var authClient = new FirebaseSimpleLogin(rootRef, function(err, user) {
  if (user) {
    var userinfo = {
      name: user.displayName,
      token: user.firebaseAuthToken,
      referrer: $.cookie("firebaseReferrer")
    };
    if (qs["utm_source"]) {
      userinfo.utm_source = qs["utm_source"];
    }
    $.ajax({
      dataType: "jsonp",
      url: "https://admin.firebase.com/auth/github",
      data: userinfo,
      success: function(data) {
        if (data && data.adminToken) {
          $('#hideoncomplete').hide();
          FirebaseAccount.setAdminToken(data.adminToken, 30);
          authClient.logout();
          if (data.created) {  
            window.location = "../welcome/";
          } else {
            window.location = "../account/";
          }
        }
      }
    })
  }
});

function onGitHubAuthClick() {
  authClient.login('github', {
    rememberMe: true,
    scope: 'user:email'
  });
  return false;
}

function onJoinBetaClick() {
  $('#invalidemail').hide();
  $("#invalidpassword").hide();
  $('#emailinbeta').hide();

  var email = $("#emailInput").val();
  var password = $("#passwordInput").val();

  if(!isValidEmail(email)) {
    $("#invalidemail").show();
    return;
  }

  if (!isValidPassword(password)) {
    $("#invalidpassword").show();
    return;
  }

  var userinfo = {
    email: email,
    password: password,
    referrer: $.cookie("firebaseReferrer")
  };
  if (qs["utm_source"]) {
    userinfo.utm_source = qs["utm_source"];
  }

  $.ajax({
    dataType: "jsonp",
    url: "https://admin.firebase.com/joinbeta",
    data: userinfo,
    success: function(data) {
      var result = data && data.success;
      if (data && data.success) {
        $('#hideoncomplete').hide();
        FirebaseAccount.login(email, password, /* rememberMe */ false, function() {
          window.location = "../welcome/";
        });
      } else if (data && data.error == "USER_EXISTS") {
        $('#emailinbeta').show();
      }
    }
  });
}

$('#emailInput, #passwordInput').on('keydown', function(e) {
  if (e.which === 13) {
    onJoinBetaClick();
    return false;
  }
});

// Enable toolbar selectors
function enableSelectors() {
  $('.selector-choice').on('click', function(event) {
    event.preventDefault();
    var $element = $(this);
    $element
      .toggleClass('selected')
      .toggleClass('orange')
      .toggleClass('white');

    return false;
  });
}

function mailcheck() {
  /* mailcheck.js integration */
  $('#suggestedEmail').on('click', function() {
    $('#emailInput').val($('#suggestedAddress').html());
    $('#emailSuggestion').hide("slow");
  });
  $('#emailInput').on('blur', function() {
    $(this).mailcheck({
      suggested: function(element, suggested) {
        $('#suggestedAddress').html(suggested.full);
        $('#emailSuggestion').show("slow");
      },
      empty: function(element) {
        $('#emailSuggestion').hide("slow");
      }
    });
  });
}

$(document).ready(function() {
  enableSelectors();
  mailcheck();

  $("#emailInput").focus();

  $("#githubAuth").click(function() {
    onGitHubAuthClick();
    return false;
  });

  // Include shim for placeholder text
  if (!Modernizr.input.placeholder){
    $("input").each(
      function(){
        if($(this).val()=="" && $(this).attr("placeholder")!=""){
          $(this).val($(this).attr("placeholder"));
          $(this).focus(function(){
            if($(this).val()==$(this).attr("placeholder")) $(this).val("");
          });
          $(this).blur(function(){
            if($(this).val()=="") $(this).val($(this).attr("placeholder"));
          });
        }
    });
  }
});

/inline_e4b2651c057e662d369fd48c2c77754b.body:
window.onAuth = window.onAuth || [];
window.onAuth.push(function(accountData) {
  onLogin();
});

var rootRef = new Firebase("firebase-accounts.firebaseio.com");
var authClient = new FirebaseSimpleLogin(rootRef, function(err, user) {
  if (user) {
    $.ajax({
      dataType: "jsonp",
      url: "https://admin.firebase.com/auth/github",
      data: {
        name: user.displayName,
        token: user.firebaseAuthToken
      },
      success: function(data) {
        if (data && data.adminToken) {
          FirebaseAccount.setAdminToken(data. adminToken, 30);
          authClient.logout();
          onLogin();
        } else {
          $("#invalidGitHubCredentials").show();
        }
      }
    })
  }
});

function onGitHubAuthClick() {
  authClient.login('github', {
    rememberMe: true,
    scope: 'user:email'
  });
  return false;
}

function onLogin() {
  var nextURL = getURLParameter("next");
  if (nextURL) {
    var nextDomain = getDomain(nextURL);
    if (nextDomain === "firebaseio.com") {
      nextURL += "#" + $.cookie("adminToken");
    }
    window.location.href = nextURL;
  } else {
    window.location = "../account/";
  }
}

function onLoginClick() {
  $("#invalidemail").hide();
  $("#invalidcredentials").hide();
  $("#invalidGitHubCredentials").hide();

  var email = $('#emailInput').val();
  if (!isValidEmail(email)) {
    $("#invalidemail").show();
    return;
  }

  var password = $('#passwordInput').val();

  var rememberMe = $('#rememberMeInput').is(":checked");
  FirebaseAccount.login(email, password, rememberMe, function(success) {
    if (success) {
      onLogin();
    } else {
      $("#invalidcredentials").show();
    }
  });
}

$(document).ready(function() {
  $("#githubAuth").click(function() {
    onGitHubAuthClick();
    return false;
  });

  var messageToken = getURLParameter("message");
  if (messageToken) {
    if (messageToken === "debugger-auth-required") {
      var message = "You must login to view your data. If this is your first time logging in, please use the ";
      message += "'(forgot password?)' link to create a password.";
    } else if (messageToken === "expired-reset-link") {
      var message = "Your password reset link has expired. If this is your first time logging in, please use the ";
      message += "'(forgot password?)' link to create a password.";
    }
    $("#message").html(message).show();
  }

  var email = getURLParameter("email");
  if (email) {
    $("#emailInput").val(email);
  }

  $('#emailInput, #passwordInput, #rememberMeInput').on('keydown', function(e) {
    if (e.which === 13) {
      onLoginClick();
      return false;
    }
  });
  $("#emailInput").focus();

  $("a.forgotPassword").click(function(el) {
    var email = $('#emailInput').val();
    if (email) {
      window.location = "../forgot_password.html?email=" + encodeURIComponent(email);
    } else {
      window.location = "../forgot_password.html";
    }
  });

  if (!Modernizr.input.placeholder){
    $("input").each(
      function(){
        if($(this).val()=="" && $(this).attr("placeholder")!=""){
          $(this).val($(this).attr("placeholder"));
          $(this).focus(function(){
            if($(this).val()==$(this).attr("placeholder")) $(this).val("");
          });
          $(this).blur(function(){
            if($(this).val()=="") $(this).val($(this).attr("placeholder"));
          });
        }
    });
  }
});

