$(function() {
  $(".doing-wrap").hover(function() {
    var img = $(this).children(".doing-image").children("img");
    var url = img.attr("src");
    img.attr("src", url.replace(".png", "-color.png"));
  }, function() {
    var img = $(this).children(".doing-image").children("img");
    var url = img.attr("src");
    img.attr("src", url.replace("-color.png", ".png"));
  });
});
