http://www-cdn.jtvnw.net/static/js/front.body:
var FRONTPAGE = {
    index: 0,
    items: [],
    interval: null,
    live_player_swf_url: null,
    express_install_swf_url: null,
    update_live_count_url: null,
    update_interval: 30 * 1000,
    anonymous: true,

    init: function() {
        var me = this;

        jQuery('.frontpage-categories-prev').on('click', function(e) {
            e.preventDefault();
            me.slideCategories('prev');
        });

        jQuery('.frontpage-categories-next').on('click', function(e) {
            e.preventDefault();
            me.slideCategories('next');
        });

        jQuery('.frontpage-channel').each( function(i, el) {
            var el = jQuery(el);
            var wrapper = el.find('.frontpage-channel-follow');
            me.displayFollowing(wrapper, el.attr('id'));
        });

        if (this.items.length > 0) {
            this.changeStream(0);
            if (this.items.length > 1){
                setInterval(this.nextStream, this.interval);
            }
        }

        setInterval(this.updateLiveCount, this.update_interval);
    },

    updateLiveCount: function() {
        jQuery.ajax({
            'url': FRONTPAGE.update_live_count_url,
            'success': function(data) {
                console.log(data);
                jQuery(".frontpage-categories-title a").text(data);
            }
        });
    },

    changeStream: function(i) {
        var channel = this.items[i];
        swfobject.embedSWF(
            this.live_player_swf_url, 
            "live_frontpage_player_flash", 
            jQuery("#live_frontpage_player_flash").outerWidth() + "px", 
            jQuery("#live_frontpage_player_flash").outerHeight() + "px", 
            "9", 
            this.express_install_swf_url,
            {
                "res": "360p",
                "channel": channel.channel,
                "hostname": "www.justin.tv"
            }, {
                "allowNetworking": "all",
                "allowScriptAccess": "always",
                "allowFullScreen": "true",
                "wmode":" transparent"
            }, ""
        );
        this.setStreamDetails(channel);
    },

    setStreamDetails: function(channel) {
        var username = channel.channel;
        var channel_url = '/' + username;
        var category_url = '/directory/' + channel.category;
        var category_src = STATIC_URL + 'images/redesign/categories/' + channel.category + '.png';
        var wrapper = jQuery('.frontpage-player-follow');
        this.displayFollowing(wrapper, username, true);
        jQuery('.frontpage-player-title').text(channel.title);
        jQuery('.frontpage-player-title').attr('href', channel_url);
        jQuery('.frontpage-player-username').text(username);
        jQuery('.frontpage-player-username').attr('href', channel_url);
        jQuery('.frontpage-player-views span').text(channel.channel_count);
        jQuery('.frontpage-player-category').attr('href', category_url);
        jQuery('.frontpage-player-category').attr('title', channel.category_display);
        jQuery('.frontpage-player-category img').attr('src', category_src);
    },

    nextStream: function() {
        this.index = this.index + 1;
        this.index = (this.index > this.items.length - 1) ? 0 : this.index;
        this.changeStream(this.index);
    },

    prevStream: function() {
        this.index = this.index - 1;
        this.index = (this.index < 0) ? this.items.length - 1 : this.index;
        this.changeStream(this.index);
    },

    displayFollowing: function(wrapper, channel, check_following) {
        var me = this;

        if (check_following) {
            wrapper.find('a').css('visibility', 'hidden');
            wrapper.find('.frontpage-unfollow-btn').attr('href', '/' + channel + '/unfollow');
            wrapper.find('.frontpage-unfollow-btn').attr('title', 'Stop following ' + channel);
            wrapper.find('.frontpage-follow-btn').attr('href', '/' + channel + '/follow');
            wrapper.find('.frontpage-follow-btn').attr('title', 'Follow ' + channel);

            var on_is_following = function(wrapper, channel) {
                wrapper.find('.frontpage-unfollow-btn').css('visibility', 'visible');
            };

            var on_not_following = function(wrapper, channel) {
                wrapper.find('.frontpage-follow-btn').css('visibility', 'visible');
            };

            if (me.anonymous) {
                on_not_following(wrapper, channel);
            } else {
                this.checkFollowingStatus(wrapper, channel, on_is_following, on_not_following);
            }
        }

        jQuery('.frontpage-follow-btn', wrapper).on('click', function(e) {
            e.preventDefault();
            if (me.anonymous) {
                window.location = '/user/login?next=/' + channel + '/follow';
            } else {
                me.follow(jQuery(this), wrapper);
            }
        });

        jQuery('.frontpage-unfollow-btn', wrapper).on('click', function(e) {
            e.preventDefault();
            if (me.anonymous) {
                window.location = '/user/login?next=/' + channel + '/unfollow';
            } else {
                me.unfollow(jQuery(this), wrapper);
            }
        });
    },

    checkFollowingStatus: function(wrapper, channel, on_is_following, on_not_following, error_callback) {
        jQuery.ajax({
            type: 'POST',
            url: '/' + channel + '/is_following',
            error: function () {
                if (error_callback) {
                    error_callback(wrapper, channel);
                }
            },
            success: function(data) {
                var is_following = (data == 'true');
                if (is_following && on_is_following) {
                    on_is_following(wrapper, channel);
                } else if (on_not_following) {
                    on_not_following(wrapper, channel);
                }
            }
        });
    },

    follow: function(element, wrapper) {
        jQuery.ajax({
            url: element.attr('href'),
            type: 'POST',
            beforeSend: function() {
                element.parent().find('.frontpage-unfollow-btn').css('visibility', 'visible');
                element.css('visibility', 'hidden');
            },
            success: function(data) {
                console.log(data);
            }
        });
    },

    unfollow: function(element, wrapper) {
        jQuery.ajax({
            url: element.attr('href'),
            type: 'POST',
            beforeSend: function() {
                element.parent().find('.frontpage-follow-btn').css('visibility', 'visible');
                element.css('visibility', 'hidden');
            },
            success: function(data) {
                console.log(data);
            }
        });
    },

    slideCategories: function(direction) {
        var outer = jQuery('.frontpage-categories-wrapper');
        var inner = jQuery('.frontpage-categories-wrapper ul');

        var inner_width = 0;
        var margin = parseInt(jQuery('.frontpage-category').css('margin-right'));
        jQuery('.frontpage-category').each(function(i, li) {
            inner_width += jQuery(li).outerWidth();
            inner_width += margin;
        });

        var clicks = 1;
        var min_ml = outer.width() - inner_width + margin;
        var ml = parseInt(inner.css('margin-left')) || 0;
        var df = parseInt(min_ml / clicks - 1);

        ml += (direction == 'prev' ? (df * -1) :  df);
        if (direction == 'prev' && ml > 0) {
            ml = 0;
        } else if (ml < min_ml) {
            ml = min_ml;
        }

        inner.animate({'margin-left': ml + 'px'});
    }

};

