var counter = 0;
function checkFrontpageHeaderAd(timeout) {
    setTimeout(function() {
        counter += 1;
        // Get the iframe ad's div indicating there's an ad present
        var iframeAdDiv = jQuery('.frontpage-header-ad iframe:first').contents().find('div');
        if (!iframeAdDiv.length) {
            // This counter is added to prevent this script
            // from continuously running when no ad was rendered
            if (counter <= 3) {
                checkFrontpageHeaderAd(timeout);
            }
            else {
                jQuery('.frontpage-header-ad').hide();
