jQuery(document).ready(function(j) {
    var set_placeholder = function(el) {
        el.val(el.data('placeholder') || '');
    };

    j(document).on('focus', '.global-header-search input', function() {
        if (j.trim(j(this).val()) === j(this).data('placeholder')) {
            j(this).val('');
        }
    }).on('blur', '.global-header-search input', function() {
        if (j.trim(j(this).val()) === '') {
            set_placeholder(j(this));
        }
    });

    j('.global-header-search input').focus().blur();

    var mp_props = {};

    if (PP.login != '') {
        mp_props['user'] = PP.login;
        mp_props['distinct_id'] = PP.user_id;
    } else {
        mp_props['user'] = 'anonymous_user';
    }

    mixpanel.track_links(".global-header-explore-pro", "dropdown_pro_link", mp_props);
    mixpanel.track_links(".global-header-explore-mobile", "dropdown_iphone_link", mp_props);
    mixpanel.track_links(".global-header-explore-broadcast", "dropdown_broadcast", mp_props);
    mixpanel.track_links(".global-header-broadcast", "broadcast_topbar", mp_props);

    j('.global-alert-close').click(function() {
        j('.global-alert').hide();
        j.ajax({
            type: "POST",
            url: '/alert/close_system_alert',
            data: {'alert_id': 'None'}
        });
    });
});

