function check_adblock() {
    try {
        if(!advertisement_loaded){
            mixpanel.track("adblock");
        }
    } catch(err){
        mixpanel.track("adblock");
    }

}
jQuery(document).ready(check_adblock);
jQuery(document).ready(function() {
    var mp_props = {};

    if (PP.login != '') {
        mp_props['user'] = PP.login;
        mp_props['distinct_id'] = PP.user_id;
        mixpanel.identify(PP.user_id);
    } else {
        mp_props['user'] = 'anonymous_user';
    }

    jQuery(".sign_in").click(function(e){
        mixpanel.track("login_click", mp_props);
    });
    jQuery("#pro_redirect_channel").click(function(e){
        mixpanel.track("medrec_cta_channel", mp_props);
    });
    jQuery("#pro_redirect_directory").click(function(e){
        mixpanel.track("medrec_cta_directory", mp_props);
    });
    jQuery(".pro_redirect_chat_line").click(function(e){
        mixpanel.track("pro_badge_chat", mp_props);
    });
    jQuery(".pro_redirect_chat_viewers").click(function(e){
        mixpanel.track("pro_badge_list", mp_props);
    });
    jQuery("#get_iphone_app").click(function(e){
        mixpanel.track("iphone_cta_mobile_mat", mp_props);
    });
    jQuery("#get_ipad_app").click(function(e){
        mixpanel.track("ipad_cta_mobile_mat", mp_props);
    });

    // header
    jQuery(".mp_dropdown_android").click(function(e){
        mixpanel.track("dropdown_android", mp_props);
    });

    // frontpage
    jQuery("a.carousel-image").click(function(e){
        var url = jQuery(this).attr('href');
        var extended_props = {};
        if(url && url.length){
            var arr = url.match(/utm_(medium|source|campaign)(%3D|=)\w+/g);
            if(arr){
                for(var i=0; i<arr.length; i++){
                    var props = arr[i].replace("%3D", "=").split("=", 2);
                    extended_props[props[0]] = props[1];
                }
            }
        }
        mixpanel.track("carousel", jQuery.extend(extended_props, mp_props));
    });
    jQuery("#pro_redirect_frontpage").click(function(e){
        mixpanel.track("medrec_cta_frontpage", mp_props);
    });
    jQuery("#broadcast_frontpage_logo,#broadcast_frontpage").click(function(e){
        mixpanel.track("broadcast_frontpage", mp_props);
    });
    jQuery("#frontpage_go_pro_logo,#frontpage_go_pro").click(function(e){
        mixpanel.track("go_pro_frontpage", mp_props);
    });
    jQuery("#get_iphone_frontpage").click(function(e){
        mixpanel.track("iphone_frontpage_ad", mp_props);
    });
    jQuery("#get_ipad_frontpage").click(function(e){
        mixpanel.track("ipad_frontpage_ad", mp_props);
    });
    jQuery("#get_android_frontpage").click(function(e){
        mixpanel.track("android_frontpage_ad", mp_props);
    });

    // footer
    jQuery("#footer_pro_link").click(function(e){
        mixpanel.track("pro_account_footer", mp_props);
    });
    jQuery("#footer_premium_link").click(function(e){
        mixpanel.track("premium_account_footer", mp_props);
    });
    jQuery("#footer_support_center_link").click(function(e){
        mixpanel.track("support_center_footer", mp_props);
    });
    jQuery("#footer_blog_link").click(function(e){
        mixpanel.track("blog_footer", mp_props);
    });

    // settings
    jQuery("#settings_pro_link").click(function(e){
        mixpanel.track("settings_pro_button", mp_props);
    });
    jQuery("#settings_premium_link").click(function(e){
        mixpanel.track("settings_premium_button", mp_props);
    });
});

