var counter = 0;

function adjustHeader(timeout) {
	setTimeout(function() {
		counter += 1;
		// Get the iframe ad's img tag
		var iframeAdImg = jQuery('#iphone_banner_in iframe:first').contents().find('img');
		if (iframeAdImg.length > 0) {
			if (iframeAdImg.height() >= 250) {
				// Adjust height of container div
		        jQuery('#iphone_banner_in').css('height', iframeAdImg.height() + 'px');
		    }
		}
		else {
			// This counter is added to prevent this script
			// from continuously running when no ad was rendered
			if (counter < 100) {
				adjustHeader(timeout);
			}
		}
	}, timeout);
}
// Run the function every 100ms
adjustHeader(100);

