var SorensonPublisher=new Object();SorensonPublisher.ver="sorenson";SorensonPublisher.loaded=false;SorensonPublisher.minVideoBitrate=25;SorensonPublisher.medVideoBitrate=128;SorensonPublisher.maxVideoBitrate=1000;SorensonPublisher.minAudioBitrate=5;SorensonPublisher.medAudioBitrate=22;SorensonPublisher.maxAudioBitrate=44;SorensonPublisher.minFPS=12;SorensonPublisher.maxFPS=15;SorensonPublisher.startBroadcast=function(){$("publisher_api_flash").publish(SorensonPublisher.server,SorensonPublisher.stream_key);}
