if($("mobile_broadcast").checked&&kbps>150){kbps=150;}
var fps=SorensonPublisher.minFPS+(kbps-SorensonPublisher.minVideoBitrate)/((SorensonPublisher.medVideoBitrate-SorensonPublisher.minVideoBitrate)/(SorensonPublisher.maxFPS-SorensonPublisher.minFPS));if(fps>SorensonPublisher.maxFPS)fps=SorensonPublisher.maxFPS;fps=Math.round(fps);var quality=(kbps<128)?80:0;var audio=$("audio_bitrate").innerHTML-0;var res=$('resolution_select').value;if(res=="160x120")var pixels=[160,120];else if(res=="320x240")var pixels=[320,240];else if(res=="640x480")var pixels=[640,480];$("publisher_api_flash").setQuality({kbps:kbps,fps:fps,quality:quality,audio:audio,pixels:pixels,favor_capture_size:false});save_setting("video",kbps);save_setting("sampling_rate",audio);save_setting("res",res);}
