function createEmptyDiv(container_id, swf_id)
{
    if(!document.getElementById(swf_id))
    {
        var div = document.createElement("div");
        div.id = swf_id;
        document.getElementById(container_id).appendChild(div);
    }    
}
function addSWF(url, container_id, swf_id, params, attrs, width, height, version)
{
    params = params || { allowScriptAccess: "always" };
    attrs = attrs || "";
    width = width || "0";    
    height = height || "0";
    version = version || "9";

    createEmptyDiv(container_id, swf_id);

    swfobject.embedSWF(unescape(url), swf_id, width, height, version, "/widgets/expressinstall.swf", "", params, attrs);
}

http://www-cdn.jtvnw.net/static/js/ad.js.body:
function pro_hide_ads() {
    if (PP.is_pro || PP.active_channel_subscriber) {
        $$('.right_col').each(function (o) {
            o.removeClassName('ad');
        });
    }
}

toggleManagedAds = function() {
  $$('.managed_ad').invoke("toggle");
}

function load_external_file(content) {
	var matchAll = new RegExp(Prototype.ScriptFragment, 'img');
	var matchSrc = new RegExp('src=\"(.+?)\"');
	var srcs=content.match(matchAll).map(function(scriptTag){return scriptTag.match(matchSrc)[1]});
	srcs.map(function(src) { $$('body')[0].insert(new Element('script',{'src':src}));});
	$$('body')[0].insert(content);
}

function show_header_banner() {
  if ($('iphone_banner') != undefined) {
    $('iphone_banner').show()
  }
}
function close_header_banner(ck_name) {
  if ($('iphone_banner') != undefined) {
    $('iphone_banner').hide()
    createCookie(ck_name,'true',60*60*24);
  }
}

function showFPopaBanner() {
  var opa_holder = $$('.fp_opa_pushdown').first();
  $(opa_holder).morph('height:66px;');

  var leaderboard = get_leaderboard();
  var tw = $('takeover_wrapper');
  if (!tw) { tw = $('directory') }
  if(leaderboard) {
      $(leaderboard).style.left = '121px';
      if (tw) { tw.addClassName('takeover'); }
  }

  if (tw) { tw.addClassName('opa_pushdown'); }
  setTimeout(FPopaAnimation, 3000);
}

function FPopaAnimation() {
  Effect.Fade('FPopaBannerImage');
  Effect.SlideDown('FPopaBannerFull');

  var opa_holder = $$('.fp_opa_pushdown').first();
  $(opa_holder).morph('height:418px;');

  move_dir_ads_to(352);
  setTimeout(hideFPopaBanner,7000);
}

function hideFPopaBanner() {
  var opa_holder = $$('.fp_opa_pushdown').first();
  $(opa_holder).morph('height:66px;');

  Effect.SlideUp('FPopaBannerFull');
  Effect.Appear('FPopaBannerImage');

  move_dir_ads_to(-352);
}

