            createQtip({object: anchor, url: dialogContentUrl, text: titleMessage || "&nbsp;"});
            anchor.click();
        };

        var showActivating = function(){
            $('#olark-activation-status').addClass('olark-activation-status-activating');
        };

        var showDeactivating = function(){
            $('#olark-activation-status').addClass('olark-activation-status-deactivating');
        };

        var showActivated = function(status){
            $('#olark-activation-status').removeClass('olark-activation-status-activating');
            $('#olark-activation-status').removeClass('olark-activation-status-deactivating');
            $('#olark-activation-status').removeClass('olark-activation-status-deactivated');
