            $('#olark-activation-status').removeClass('olark-activation-status-deactivating');
            $('#olark-activation-status').removeClass('olark-activation-status-working');
            $('#olark-activation-status').addClass('olark-activation-status-deactivated');
            $('#olark-activation-status').removeClass('olark-activation-status-activated');
            $('#todo .chatbox').removeClass('complete');
            $.get('/dashboard/get_number_of_steps_to_go_json', function(data){
                $('#number_of_steps_to_go').html(data);
            });
        };

        $('#olark-activate-on-shopify-link').click(function(){
