            var handleSuccess = function(){
                showActivated();
            };
            var handleFailure = function(next_action){
                showDeactivated();
                popupDialog('/shopify/' + next_action, $('#olark-shopify-dialog-failed-text').text());
            };
            $.ajax({
                dataType: 'json',
                type: 'GET',
                url: '/shopify/activate_on_shopify',
                success: function(data){
                    if (data.success == true) {
                        handleSuccess();
                    } else {
                        handleFailure(data.next_action);
                    }
                },
                error: function(){
                    handleFailure('dialog_unknown_error');
                    if (window.console && console.error) {
                        console.error("unknown failure for activate_on_shopify #shopify", arguments);
                    }
                }
            });
            return false;
        });

        $('#olark-deactivate-on-shopify-link').click(function(){
            showDeactivating();
            var handleSuccess = function(){
                showDeactivated();
            };
            var handleFailure = function(next_action){
                showActivated();
                popupDialog('/shopify/' + next_action, $('#olark-shopify-dialog-failed-text').text());
            };
            $.ajax({
                dataType: 'json',
                type: 'GET',
                url: '/shopify/deactivate_on_shopify',
                success: function(data){
                    if (data.success == true) {
                        handleSuccess();
                    } else {
                        handleFailure(data.next_action);
                    }
                },
                error: function(){
                    handleFailure('dialog_unknown_error');
                    if (window.console && console.error) {
                        console.error("unknown failure for deactivate_on_shopify #shopify", arguments);
                    }
                }
            });
            return false;
        });

        $('#olark-activating-on-shopify-link').click(function(){
            return false;
        });

        $('#olark-deactivating-on-shopify-link').click(function(){
            return false;
        });

        $('#show_on_customizer').click(function(){
            $('#shopify_install_manually').toggle();
            return false;
        });

    })();




}); // end document.ready

