{{#message}}
  {{#created_by_user}}
  <div class="ic_header">
    <div class="ic_color_tab"></div>
    <h1>{{> s_message_to_app}}</h1>
    <a href="#" class="ic_back"><span>{{> s_back}}</span></a>
    <a href="#" class="ic_close_modal"><span>{{> s_close}}</span></a>
  </div>
  <div id="IntercomMessageView" class="ic_first_message_is_user_response {{#has_comments}} ic_has_comments{{/has_comments}}">
    <div class="ic_messages_wrapper">
      <ul class="ic_comments">
        {{> message}}
        {{#comments}}
          {{#message}}
            {{> message}}
          {{/message}}
        {{/comments}}
      </ul>
    </div>

    {{> message_reply_form}}    

  </div>
  {{/created_by_user}}
  {{^created_by_user}}
  <div class="ic_header{{#has_avatar}} ic_has_avatar{{/has_avatar}}">
    <div class="ic_color_tab"></div>
    {{#has_avatar}}
      <img src="{{#from}}{{#avatar}}{{square_50}}{{/avatar}}{{/from}}" alt="{{> s_avatar_for_person}}" class="ic_avatar" />
    {{/has_avatar}}
    <h1>{{> s_message_from_app}}</h1>
    <a href="#" class="ic_back"><span>{{> s_back}}</span></a>
    <a href="#" class="ic_close_modal"><span>{{> s_close}}</span></a>
  </div>
  <div id="IntercomMessageView" class="{{#has_comments}} ic_has_comments{{/has_comments}}">
    <div class="ic_messages_wrapper">
      <div class="ic_message">
        <blockquote class="ic_message_body">
          {{#has_subject}}
          <h4 class="ic_subject">{{{subject}}}</h4>
          {{/has_subject}}
          {{{html}}}
        </blockquote>
      </div>
      <ul class="ic_comments">
        {{#comments}}
          {{#message}}
            {{> message}}
          {{/message}}
        {{/comments}}
      </ul>
    </div>

    {{> message_reply_form}}    

  </div>
  {{/created_by_user}}
{{/message}}
',notification_message_view:'{{#message}}
  <div class="ic_notification_message{{#has_avatar}} ic_has_avatar{{/has_avatar}} ic_notification_message_inline_reply" data-conversation-id="{{id}}">
    <div class="ic_color_tab"></div>
    {{#has_avatar}}
      <div class="ic_avatar_wrapper">
        <img src="{{#from}}{{#avatar}}{{square_50}}{{/avatar}}{{/from}}" alt="{{> s_avatar_for_person}}" class="ic_avatar" />
      </div>
    {{/has_avatar}}
    <div class="ic_notification_inline_reply">
      <div class="ic_message_body_wrapper">
        <a href="#" class="ic_close_notification"><span>{{> s_close}}</span></a>
        <div class="ic_message_body">
          <div class="ic_content_wrapper">
            {{#has_subject}}
              <h4 class="ic_subject">{{{subject}}}</h4>
            {{/has_subject}}
            {{{html}}}
          </div>
        </div>
      </div>
      <div class="ic_notification_inline_composer">
        <form accept-charset="UTF-8" action="" method="post">
          <textarea id="ic_notification_inline_comment_body" name="" type="text" placeholder="{{> s_write_a_reply }}&hellip;"></textarea>
          <button id="ic_notification_inline_comment_commit" name="commit" type="submit" value="{{> s_reply}}" class="ic_visuallyhidden"><span>{{> s_reply}}</span></button>
        </form>
      </div>
      <div class="ic_notification_inline_feedback ic_hidden clearfix">
         <span>{{> s_reply_sent}}</span>
         {{> powered_by }}
      </div>
    </div>
  </div>
{{/message}}',message_list:'{{%IMPLICIT-ITERATOR}}
<div id="IntercomMessageListView" class="ic_pane">

  <div class="ic_header">
    <div class="ic_color_tab"></div>
    <h1>{{> s_messages_with_app}}</h1>
    <a href="#IntercomNewMessageContainer" class="ic_new_message"><span>{{> s_new_message}}</span></a>
    <a href="#" class="ic_close_modal"><span>{{> s_close}}</span></a>
  </div>

  <div class="ic_table_wrapper">
    <table cellpadding="0" cellspacing="0">
      <tbody>
      {{#threads}}
      <tr class="{{is_notification}}{{#is_first}}ic_first{{/is_first}} {{#unread}} ic_unread{{/unread}} {{#created_by_user}} ic_last_response_is_from_app_user{{/created_by_user}}" data-conversation-id="{{id}}">
        <th class="ic_user">
          <a href="#" class="ic_view_message" data-conversation-id="{{id}}">
            {{#has_avatar}}
              <img src="{{#from}}{{#avatar}}{{square_25}}{{/avatar}}{{/from}}" />
            {{/has_avatar}}
            <span>
              {{^created_by_user}}
                {{#from}}{{name}}{{/from}}
                {{^from}}{{> s_you}}{{/from}}
              {{/created_by_user}}
              {{#created_by_user}}
                {{> s_you}}
              {{/created_by_user}}
            </span>
          </a>
        </th>
        <td class="ic_said">
          <a href="#" class="ic_view_message" data-conversation-id="{{id}}"><span>{{title}}</span></a>
        </td>
        <td class="ic_time">
          <a href="#" class="ic_view_message" data-conversation-id="{{id}}"><span class="ic_time">{{> s_date}}</span></a>
        </td>
      </tr>
      {{/threads}}
      </tbody>
    </table>
  </div>
</div>
',new_message:'<div id="IntercomNewMessageContainer" class="ic_pane">
  <div class="ic_header">
    <div class="ic_color_tab"></div>
    <h1>{{> s_new_message_to_app}}</h1>
    <a href="#" class="ic_back"><span>{{> s_back}}</span></a>
    <a href="#" class="ic_close_modal"><span>{{> s_close}}</span></a>
  </div>
  <div class="ic_form_wrapper">
    <form accept-charset="UTF-8" action="{{#options}}{{conversation_create_url}}{{/options}}" method="post">
      <div style="margin:0;padding:0;display:inline">
        <input name="utf8" type="hidden" value="â" />
      </div>
      <input id="email" name="email" type="hidden" value="{{#options}}{{email}}{{/options}}" />
      <input id="user_id" name="user_id" type="hidden" value="{{#options}}{{user_id}}{{/options}}" />
      <input id="app_id" name="app_id" type="hidden" value="{{#options}}{{app_id}}{{/options}}" />
      <textarea id="newMessageBody" name="body">{{new_message_draft}}</textarea>
      <button id="message_commit" name="commit" type="submit" value="{{> s_send}}"><span>{{> s_send}}</span></button>
      {{> powered_by }}
    </form>
  </div>
</div>'},template_functions:{func_string_to_number:function(){return function(e,t){return parseInt(t(e),10)}},func_month_to_text:function(){return function(e,t){return t("{{> s_months_"+["jan","jan","feb","mar","apr","may","jun","jul","aug","sep","oct","nov","dec"][parseInt(t(e),10)]+"}}")}}},template_partials:{message:'<li class="{{#created_by_user}}ic_user_response {{/created_by_user}} ic_index_{{index}} {{#uploading}}ic_uploading{{/uploading}}" {{#is_comment}}data-comment-id="{{id}}"{{/is_comment}}>
  <blockquote class="ic_comment_body">
    {{{html}}}

    {{#has_attachments}}
    <div class="ic_message_attachments">
      <strong class="ic_attachments_label">Attachments:</strong>
      {{#attachments}}
      <a href="{{url}}" target="_blank">{{file_name}}</a>
      {{/attachments}}
    </div>
    {{/has_attachments}}
  </blockquote>
  <div class="ic_user{{#has_avatar}} ic_has_avatar{{/has_avatar}}">
    <span class="ic_avatar">
      {{#has_avatar}}
        <img src="{{#from}}{{#avatar}}{{square_25}}{{/avatar}}{{/from}}" alt="{{> s_avatar_for_person}}" />
      {{/has_avatar}}
    </span>
    <span class="ic_username">
      {{#created_by_user}}
        {{> s_you}}
      {{/created_by_user}}
      {{^created_by_user}}
        {{#has_name}}
          {{> s_admin_from_app}}
        {{/has_name}}
        {{^has_name}}
          {{> s_from_app}}
        {{/has_name}}
      {{/created_by_user}}
    </span>
  </div>
</li>
