$(document).ready(function(){
if ($('.inbox_badge').text() > 0) {
$('.inbox_badge').addClass('new_message');
}
var toggleModals;
toggleModals = function() {
$(".toggle_signup_login").click(function(e) {
if ($("#myModal").hasClass("loginmodal")) {
$('#myModal').html("").removeClass('modal').removeClass("loginmodal");
loadSignupModal();
} else {
$('#myModal').html("").removeClass('modal').removeClass("signupmodal");
loadLoginModal();
}
});
};
var setupAjaxHeader;
setupAjaxHeader = function(){
$.ajaxSetup({
beforeSend: function( xhr ) {
var token = $('meta[name="csrf-token"]').attr('content');
if (token) xhr.setRequestHeader('X-CSRF-Token', token);
}
});
}
var postLoginForm;
postLoginForm = function() {
setupAjaxHeader();
var valuesToSubmit;
valuesToSubmit = $("#user_signin_form").serialize();
$("#user_signin_form_trigger").html("please wait...");
return $.ajax({
type: 'post',
url: $("#user_signin_form").attr('action'),
data: valuesToSubmit,
dataType: 'json',
success: function(data) {
var button_text;
if (data.success === true) {
if (data != null) {
console.log("did not get login error + data: " + data);
}
window.location.href = gon.site_url + data.location;
} else {
console.log("got login error + data: " + data);
$(".devise_errors").createWarning("Please check your credentials");
button_text = $("#user_signin_form_trigger").attr("data-text");
$("#user_signin_form_trigger").html(button_text);
}
},
error: function() {
var button_text;
if (typeof data !== "undefined" && data !== null) {
console.log("failure data" + data);
}
$(".devise_errors").createWarning("Please check your credentials");
button_text = $("#user_signin_form_trigger").attr("data-text");
$("#user_signin_form_trigger").html(button_text);
}
});
};
var loadLoginModal;
loadLoginModal = function() {
return $.ajax({
url: '/signin/',
success: function(data) {
$('#myModal').html(data.data);
$('#myModal').addClass('modal');
$('#myModal').css({
'border-top': '5px solid rgba(93,210,206,1)'
}).attr('data-height',460).attr('data-width',520).attr('tabindex',1).addClass("loginmodal");
$('#myModal').modal().show();
toggleModals();
$(document).on("keyup", "#user_password", function(event) {
if ((event.keyCode === 13) && (!event.shiftKey)) {
event.preventDefault();
postLoginForm();
}
});
return $("#user_signin_form_trigger").click(function(e) {
e.preventDefault();
return postLoginForm();
});
},
error: function(data) {
return console.log(data);
}
});
};
var loadSignupModal;
loadSignupModal = function() {
return $.ajax({
url: '/users/sign_up/',
success: function(data) {
$('#myModal').html(data.data);
$('#myModal').addClass('modal');
$('#myModal').css({
'border-top': '5px solid rgba(248,114,66,1)'
}).attr('data-height',560).attr('data-width',520).attr('tabindex',1).addClass('signupmodal');
$('#myModal').modal().show();
toggleModals();
},
error: function(data) {
return console.log(data);
}
});
};
$('.loginwindow').click(function(e, data) {
e.preventDefault();
loadLoginModal();
});
$('.signupwindow').click(function(e, data) {
e.preventDefault();
loadSignupModal();
});
$('.toggle_signup_login').click(function(e, data) {
e.preventDefault();
toggleModals();
});
});

