	function recordClicksOnPage(link, type, action) {
  		try {
    			pageTracker._trackEvent(type, action);
    			if (link && link.href) {
	    			setTimeout('document.location = "' + link.href + '"', 50);
	    		}
  		}
		catch(err) { }
	}

        function viewTracking() {
            var domain=window.location.hostname;
            recordClicksOnPage(this, 'view', domain);
        }
        window.onload=viewTracking;

https://www.xobni.com/javascripts/jquery.body:
// Video Lightbox
// For pages on xobni.com only (requires CSS from /styles/global.css)
// If embedding a SWF directly, this requires swfobject to be called first.

(function($){
	$.fn.videoLightbox = function(options) {
		return this.each(function() {

			// add #videoclose, #videoplayer, and #lightbox to the DOM only if they aren't here already
			if ($('#videolightbox').length == 0) {
				$('#main').after("<div id='videolightbox' class='lightbox'></div>")
				$('#videolightbox').click (function() {
					$('#videoplayer').html("");
					$('#videoplayer, #videolightbox, #videoclose').fadeOut();
				});
			}
			if ($('#videoplayer').length == 0) {
				$('#videolightbox').after("<div id='videoplayer' style='border: solid 3px white;' ></div>");
			}
			if ($('#videoclose').length == 0) {
				$('#videoplayer').after("<div id='videoclose' class='lightboxclose'></div>")
				$('#videoclose').click (function() {
					$('#videoplayer').html("");
					$('#videoplayer, #videolightbox, #videoclose').fadeOut();
				});
			}


			var settings = {
				'movie': 		"/media/plus/xobni_plus_07.swf",
				'width': 		720,
				'height':		540,
				'type':			"swf", // embed a "swf" directly, or use an "html" (or php) file in an iframe
				'videoPath':	"/media/walkthroughs/xobni_features_02/", // optional for swf
				'htmlSource':   "", // html for "markup" type
				'bgcolor':		"#000000"
			};
			if (options) $.extend(settings, options);

			$(this).click(function(e) {
                e.preventDefault();

				var x = ($(window).width() / 2) - (settings.width / 2);
				var y = $(window).scrollTop() + ($(window).height() / 2) - (settings.height / 2);
				var w = settings.width;
				var h = settings.height;

				if (settings.type == "html") {
					$('#videoplayer').html("<iframe scrolling='no' frameborder='0' src='" + settings.movie + "'></iframe>");
				} else if (settings.type == "markup") {
				    $('#videoplayer').html(settings.htmlSource);
				} else {
					$('#videoplayer').html("<div id='videoswf'></div>");
					if (swfobject.hasFlashPlayerVersion("8.0.0")) {

						var flashVars = {
							videoPath: escape(settings.videoPath)
						};

						var params = {
							width: w,
							height: h,
							quality: "high",
							pluginspage: "http://www.adobe.com/go/getflashplayer",
							align: "center",
							play: "true",
							loop: "false",
							scale: "noorder",
							wmode: "opaque",
							devicefont: "false",
							bgcolor: settings.bgcolor,
							menu: "false",
							allowFullScreen: "false",
							allowScriptAccess: "always",
							salign: "lt"
						}

						var attributes = {
							name: "videoswf",
							id: "videoswf",
							swLiveConnect: "true"
						}

						swfobject.embedSWF(settings.movie, "videoswf", w, h, "8.0.0", null, flashVars, params, attributes, function(e){
							if(!e.success){
								$('#videoplayer').css("background-color", "black").css("color", "white").html("Sorry, you do not have the latest version of Flash installed.");
							}
						});
					} else {
						$('#videoplayer').css("background-color", "black").css("color", "white").html("Sorry, you do not have the latest version of Flash installed.");
					}
				}

				setTimeout(function() {
					$('#videoplayer').css({
						top: y,
						left: x,
						width: w,
						height: h
					});
					$('#videoplayer iframe').css({
						width: w,
						height: h,
						top: 0,
						left: 0
					});
					$('#videoclose').css({
						top: y - 10,
						left: x + w - 10
					});
					$('#videolightbox').css({
						width: $(document).width(),
						height: $(document).height(),
						opacity: .3
					});
					$('#videoplayer, #videolightbox, #videoclose').fadeIn("fast");
				}, 50);
			});
		});
	};
})(jQuery);

/**
 * jQuery lightBox plugin
 * This jQuery plugin was inspired and based on Lightbox 2 by Lokesh Dhakar (http://www.huddletogether.com/projects/lightbox2/)
