	$.fn.videoLightbox = function(options) {
		return this.each(function() {

			// add #videoclose, #videoplayer, and #lightbox to the DOM only if they aren't here already
			if ($('#videolightbox').length == 0) {
				$('#main').after("<div id='videolightbox' class='lightbox'></div>")
				$('#videolightbox').click (function() {
					$('#videoplayer').html("");
					$('#videoplayer, #videolightbox, #videoclose').fadeOut();
				});
			}
			if ($('#videoplayer').length == 0) {
				$('#videolightbox').after("<div id='videoplayer' style='border: solid 3px white;' ></div>");
			}
			if ($('#videoclose').length == 0) {
				$('#videoplayer').after("<div id='videoclose' class='lightboxclose'></div>")
				$('#videoclose').click (function() {
					$('#videoplayer').html("");
					$('#videoplayer, #videolightbox, #videoclose').fadeOut();
				});
			}


			var settings = {
				'movie': 		"/media/plus/xobni_plus_07.swf",
				'width': 		720,
				'height':		540,
				'type':			"swf", // embed a "swf" directly, or use an "html" (or php) file in an iframe
				'videoPath':	"/media/walkthroughs/xobni_features_02/", // optional for swf
				'bgcolor':		"#000000"
			};
			if (options) $.extend(settings, options);

			$(this).click(function(e) {
                e.preventDefault();

				var x = ($(window).width() / 2) - (settings.width / 2);
				var y = $(window).scrollTop() + ($(window).height() / 2) - (settings.height / 2);
				var w = settings.width;
				var h = settings.height;

				if (settings.type == "html") {
					$('#videoplayer').html("<iframe scrolling='no' frameborder='0' src='" + settings.movie + "'></iframe>");
				} else {
					$('#videoplayer').html("<div id='videoswf'></div>");
					if (swfobject.hasFlashPlayerVersion("8.0.0")) {

						var flashVars = {
							videoPath: escape(settings.videoPath)
						};

						var params = {
							width: w,
