Carousel = (function() {
	var m_imagesList = [];
	var m_imagesLoaded = []; // does the billboard already exist in the DOM
	var m_imagesZOrder = 0; // the stacking order: no maximum
	var m_imageIndex = 0; // the current image ID (in imagesList): max is imagesList.length

	var m_baseURL = "";
	var m_timer = null;
	var m_timerSpeed = 5000;

	function incrementSlide() {
		m_imageIndex++;
		if (m_imageIndex == m_imagesList.length) {
			m_imageIndex = 0;
		}
		if (m_imagesLoaded[m_imageIndex]) {
			animateSlide();
		} else {
			loadSlide();
		}
	}

	function loadSlide() {
		var imageId = m_imagesList[m_imageIndex];

		$('#companyboard').prepend("<img id=\"billboard" + m_imageIndex + "\" src=\"" + m_baseURL + imageId + "_large.jpg\" width=620 height=280 border=0 />");

		$('#billboard' + m_imageIndex).css({
											top: 0,
											left: 0,
											display: "none",
											position: "absolute"
										})
										.load(function() {
											m_imagesLoaded[m_imageIndex] = true;
											$(this).unbind('load');
											animateSlide();
										});
	}

	function animateSlide() {

		clearTimeout(m_timer);
		m_imagesZOrder++;

		// set up the selected thumbnails
		$('#companycarousel img').removeClass("selected");
		$('#' + m_imagesList[m_imageIndex] + '_carousel').addClass("selected");

		$('#billboard' + m_imageIndex).css("zIndex", m_imagesZOrder + 1);
		$('#billboard' + m_imageIndex).fadeIn("slow", function() {
			// hide all billboard images except for the selected one
			$('#companyboard img').hide();
			$('#billboard' + m_imageIndex).show();
			m_timer = setTimeout(incrementSlide, m_timerSpeed);
		});


	}

	return {
		select: function(id) {
			m_imageIndex = parseInt(id);
			if (m_imagesLoaded[m_imageIndex]) {
				animateSlide();
			} else {
				loadSlide();
			}
		},

		initialize: function(imagesList, baseURL) {
			m_imagesList = imagesList;
			m_baseURL = baseURL;

			// populate the carousel 
			var html = "";
			for (var i in m_imagesList) {
				html += "<img id=\"" + m_imagesList[i] + "_carousel\" src=\"" + m_baseURL + m_imagesList[i] + "_small.jpg\" width=32 height=32 border=0 onclick=\"Carousel.select('" + i + "');\" />";
			}
			$('#companycarousel').html(html);
			loadSlide();
		}

	}
})();

