	$(document).ready(function() {        

		// set up lightboxes for product videos
		$('#video_pro').videoLightbox({
			"movie": "/media/pro/xobnipro_pro3.swf",
			"type": "swf",
			"width": 720,
			"height": 540
		});

		// mini-gadget carousel
		(function() {
		    var gadgetPositions = [0, -234, -468];
		    var gadgetIndex = 0;
            setInterval(function() {
                gadgetIndex++;
                if (gadgetIndex == gadgetPositions.length) {
                    gadgetIndex = 0;
                }
                $('#gadget_slider').animate({
                    marginLeft: gadgetPositions[gadgetIndex]
                }, 500);
            }, 5000);
		})();

		Clinger = (function() {
    		var navbarY = $('div.clinger').offset().top;
    		var anchorCheck = true;

    		function checkAnchors() {
       		    var anchorSelected = "", anchorCandidate = "";
    		    $('div.clinger-target').each(function() {
    		        if ($(this).is(":in-viewport")) {
    		            anchorCandidate = this.id;
    		        }
    		    });
    		    if (anchorCandidate != anchorSelected) {
    		        anchorSelected = anchorCandidate;
    		        $('#clinger_anchors a').removeClass("selected");
    		        $('#clinger_anchors a[href="#' + anchorSelected + '"]').addClass("selected");
    		    }
    		}
    		$(window).scroll(function() {
    			var windowY = $(this).scrollTop();
    			if (windowY > navbarY) {
    				$('div.clinger').css({
    					position: "fixed",
    					top: 0,
    					width: "850px"
    				});
    				$('#main').css("padding-top", "80px");
    			} else {
    				$('div.clinger').css("position", "inherit");
    				$('#main').css("padding-top", "0");
    			}
                if (anchorCheck) {
                    checkAnchors();
                }
    		});
    		$('#clinger_anchors a').click(function(e) {
    		    e.preventDefault();
    		    anchorCheck = false;
    		    var $that = $(this);
                $('html, body').animate({
                    scrollTop: $($(this).attr("href")).position().top - 80
                }, 200, function() {
                    anchorCheck = true;
                    $('#clinger_anchors a').removeClass("selected");
    		        $that.addClass("selected");

                });
    		});
		})();
	});

